CREATE OR REPLACE PROCEDURE calcular_carboplatina_orc ( nr_seq_orcamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_pessoa_fisica_w	varchar(10);
dt_nascimento_w		timestamp;
qt_idade_w		smallint;
ie_sexo_w		varchar(1);
qt_peso_w		varchar(10);
qt_mg_carboplatina_w	double precision;
qt_clearance_w		double precision;
qt_auc_w		real;
qt_creatinina_w		real;


BEGIN 
qt_mg_carboplatina_w	:= 0;
 
begin 
select 	cd_pessoa_fisica, 
	coalesce(qt_peso,0), 
	coalesce(qt_auc,0), 
	coalesce(qt_creatinina,0) 
into STRICT	cd_pessoa_fisica_w, 
	qt_peso_w, 
	qt_auc_w, 
	qt_creatinina_w 
from 	orcamento_paciente 
where	nr_sequencia_orcamento = nr_seq_orcamento_p;
exception 
	when others then 
	cd_pessoa_fisica_w:= '0';
end;
 
if (cd_pessoa_fisica_w <> '0') then 
 
	select	dt_nascimento, 
		ie_sexo 
	into STRICT	dt_nascimento_w, 
		ie_sexo_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
 
	select	(obter_idade(dt_nascimento_w,clock_timestamp(),'A'))::numeric  
	into STRICT	qt_idade_w 
	;
 
	if (qt_peso_w > 0) and (qt_creatinina_w > 0) then 
		begin 
		qt_clearance_w	:= dividir(((140 - qt_idade_w) * qt_peso_w) , (7.2 * qt_creatinina_w));
 
		qt_clearance_w	:= qt_clearance_w * 0.10;	
 
		if (ie_sexo_w = 'F') then	 
			qt_clearance_w	:= qt_clearance_w * 0.85;	
		end if;
 
		qt_mg_carboplatina_w	:= round((qt_auc_w * (qt_clearance_w + 25)),2);
 
		qt_clearance_w	:= round((qt_clearance_w)::numeric,2);
		end;
	end if;
	 
	update	orcamento_paciente 
	set 	QT_CLEARANCE_CREATININA = qt_clearance_w, 
		QT_MG_CARBOPLATINA = qt_mg_carboplatina_w 
	where	nr_sequencia_orcamento = nr_seq_orcamento_p;
 
	CALL atualizar_dose_onc_orcamento(nr_seq_orcamento_p, nm_usuario_p);
	 
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
