SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE calcular_classif_etapa_proj ( nr_seq_cronograma_p bigint, nr_seq_etapa_sup_p bigint, cd_classif_sup_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_etapa_w		bigint;
qt_loop_w		bigint := 0;
cd_classificacao_w	varchar(255);

c01 CURSOR FOR
SELECT	nr_sequencia
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p
and	nr_seq_superior = nr_seq_etapa_sup_p
and	coalesce(ie_situacao,'A') = 'A'
order by
	nr_seq_apres;


BEGIN
if (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (nr_seq_etapa_sup_p IS NOT NULL AND nr_seq_etapa_sup_p::text <> '') and (cd_classif_sup_p IS NOT NULL AND cd_classif_sup_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into nr_seq_etapa_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		qt_loop_w := qt_loop_w + 1;
		if (qt_loop_w < 10) then
			begin
			cd_classificacao_w := cd_classif_sup_p || '.0' || to_char(qt_loop_w);
			end;
		else
			begin
			cd_classificacao_w := cd_classif_sup_p || '.' || to_char(qt_loop_w);
			end;
		end if;

		update	proj_cron_etapa
		set	cd_classificacao = cd_classificacao_w,
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_etapa_w;

		CALL calcular_classif_etapa_proj(nr_seq_cronograma_p, nr_seq_etapa_w, cd_classificacao_w, nm_usuario_p);
		end;
	end loop;
	close c01;
	end;
end if;
--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
