SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE calcular_custo_comp_receita (NR_SEQUENCIA_P bigint) AS $body$
DECLARE


nr_sequencia_comp_w		bigint;
qt_componente_w			double precision;
qt_conversao_w			double precision;
vl_custo_w				double precision;
vl_custo_total_w			double precision;
cd_material_w			integer;
cd_estabelecimento_w		integer;
nr_seq_gen_alim_w               bigint;
qt_dias_ult_compra_w		bigint;

C01 CURSOR FOR
	SELECT  n.nr_sequencia,
		coalesce(nr_seq_gen_alim_sub,n.nr_seq_gen_alim),
		--g.cd_material,
		n.qt_componente
		--NVL(g.qt_conversao,1)
	FROM	nut_genero_alim g,
		nut_rec_real_comp n
	WHERE	n.nr_seq_gen_alim	= g.nr_sequencia
	AND	n.nr_seq_rec_real	= nr_sequencia_p;


BEGIN
SELECT		coalesce(MAX(cd_estabelecimento),0)
INTO STRICT		cd_estabelecimento_w
FROM		nut_receita_real
WHERE		nr_sequencia	= nr_sequencia_p;



IF (cd_estabelecimento_w > 0) THEN
	BEGIN

	qt_dias_ult_compra_w := obter_valor_param_usuario(912,27,obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, cd_estabelecimento_w);

	OPEN C01;
	LOOP
		FETCH C01 INTO
			nr_sequencia_comp_w,
			nr_seq_gen_alim_w,
			--cd_material_w,
			qt_componente_w;
			--qt_conversao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		BEGIN

		select 	cd_material,
			coalesce(qt_conversao,1)
		into STRICT	cd_material_w,
			qt_conversao_w
		from	nut_genero_alim
		where 	nr_sequencia = nr_seq_gen_alim_w;

		-- Retirado na OS 642832 - 02/10/2013
		/*SELECT NVL(MAX(c.vl_custo),0)
		INTO	vl_custo_w
		FROM	custo_mensal_material c
		WHERE c.cd_estabelecimento	= cd_estabelecimento_w
		  AND	c.cd_material		= cd_material_w
		  AND	c.ie_tipo_custo		= 'R'
		  AND	c.dt_referencia		=
			(SELECT MAX(dt_referencia)
			FROM	custo_mensal_material c
			WHERE c.cd_estabelecimento	= cd_estabelecimento_w
			  AND	c.cd_material		= cd_material_w
			  AND	c.ie_tipo_custo		= 'R');*/
		SELECT	coalesce(obter_dados_ultima_compra(cd_estabelecimento_w, cd_material_w,'VE'),0)
		INTO STRICT	vl_custo_w
		;

		vl_custo_total_w	:= vl_custo_w * qt_componente_w / qt_conversao_w;
		UPDATE	Nut_Rec_Real_Comp
		SET		vl_custo		= vl_custo_total_w
		WHERE		nr_sequencia	= nr_sequencia_comp_w;
		END;
	END LOOP;
	CLOSE C01;
	END;
ELSE
	--'Estabelecimento n√£o encontrado na receita.
	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(261176);
END IF;
COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
