CREATE OR REPLACE PROCEDURE calcular_desconto_neg_cr ( nr_seq_negociacao_p bigint, pr_desconto_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_total_w		double precision;
vl_total_desc_w		double precision;



BEGIN
if (nr_seq_negociacao_p IS NOT NULL AND nr_seq_negociacao_p::text <> '') and (pr_desconto_p IS NOT NULL AND pr_desconto_p::text <> '') then

	/* Pimeiro zerar o desconto */

	update	negociacao_cr
	set	vl_desconto	= 0
	where	nr_sequencia	= nr_seq_negociacao_p;

	select	obter_valores_negociacao_cr(nr_seq_negociacao_p,'VT')
	into STRICT	vl_total_w
	;

	vl_total_desc_w	:= dividir_sem_round((vl_total_w * pr_desconto_p)::numeric, 100);

	update	negociacao_cr
	set	vl_desconto	= vl_total_desc_w
	where	nr_sequencia	= nr_seq_negociacao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
