CREATE OR REPLACE PROCEDURE calcular_etapas_interv_npt ( cd_intervalo_p text, qt_hora_inf_p bigint, qt_hora_fase_p INOUT bigint, nr_etapas_p INOUT bigint) AS $body$
DECLARE


ie_operacao_w			  varchar(5);
qt_operacao_w		 	  double precision;


BEGIN

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	Select	max(a.ie_operacao),
		max(a.qt_operacao)
	into STRICT 	ie_operacao_w,
		qt_operacao_w
	from	intervalo_prescricao a
	where	a.cd_intervalo = cd_intervalo_p;


	if (ie_operacao_w = 'X') then
		qt_hora_fase_p	:= dividir(24,qt_operacao_w);
	elsif (ie_operacao_w = 'H') then
		qt_hora_fase_p	:= qt_operacao_w;
	elsif (ie_operacao_w in ('F','V')) then
		qt_hora_fase_p	:= dividir(qt_hora_inf_p,qt_operacao_w);
	else
		qt_hora_fase_p	:= dividir(qt_hora_inf_p,qt_operacao_w);
	end if;

	if (qt_hora_fase_p > qt_hora_inf_p) then
		qt_hora_fase_p	:= qt_hora_inf_p;
	end if;

	nr_etapas_p	:= trunc(dividir(qt_hora_inf_p,qt_hora_fase_p));

	if (nr_etapas_p > 4 )	 then
		nr_etapas_p  := 4;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
