SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE calcular_gotejamento_nut_pac ( nr_sequencia_p bigint) AS $body$
DECLARE


qt_volume_w		double precision;
qt_horas_w		bigint;
nr_prescricao_w		bigint;
qt_resultado_w		double precision;
ie_usar_hrs_prescr_w	varchar(1);


BEGIN

CALL Wheb_assist_pck.set_informacoes_usuario(obter_estabelecimento_ativo, obter_perfil_ativo, obter_Usuario_Ativo);

ie_usar_hrs_prescr_w	:= Wheb_assist_pck.obterParametroFuncao(924,856);

-- Busca o volume
select	max(obter_volume_nut_pac(b.nr_sequencia)),
	max(coalesce(a.nr_horas_validade,24)),
	max(a.nr_prescricao)
into STRICT	qt_volume_w,
	qt_horas_w,
	nr_prescricao_w
from	nut_paciente b,
	prescr_medica a
where	b.nr_prescricao	= a.nr_prescricao
and	b.nr_sequencia	= nr_sequencia_p;

--Se for S utuliza NR_HORAS_VALIDADE da PRESCR_MEDICA (OS 753355)
if (ie_usar_hrs_prescr_w = 'N') then
	qt_horas_w	:= 24;
end if;

-- Realiza o calculo do gotejamento
if (qt_volume_w IS NOT NULL AND qt_volume_w::text <> '') and (qt_horas_w IS NOT NULL AND qt_horas_w::text <> '') then
	qt_resultado_w := (qt_volume_w / qt_horas_w);
else
	qt_resultado_w := 0;
end if;

-- Altera o registro
update	nut_paciente
set	qt_gotejamento_npt	= coalesce(round(qt_resultado_w),0)
where	nr_sequencia		= nr_sequencia_p
and	nr_prescricao		= nr_prescricao_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
