CREATE OR REPLACE PROCEDURE calcular_gotejamento_ordem ( Tipo_dosagem_p text, qt_tempo_aplicacao_p bigint, qt_volume_total_p bigint, vl_retorno_p INOUT bigint) AS $body$
DECLARE




Gotas_W		double precision;
Tempo_W 	double precision;
Resultado_W	double precision;


BEGIN

if (upper(Tipo_dosagem_p) = 'GTM') then
    begin
    Gotas_w	:= 20;
    Tempo_w	:= 60;
    end;

elsif (upper(Tipo_dosagem_p) = 'MLH') then
    begin
    Gotas_w	:= 1;
    Tempo_w	:= 1;
    end;

else
    begin
    Gotas_w	:= 50;
    Tempo_w	:= 60;
    end;
end if;

Resultado_w	:= 0;

Resultado_w                 := Dividir(Dividir((Round(qt_volume_total_p) * Gotas_w) ,
                              qt_tempo_aplicacao_p) , Tempo_w);
if (Resultado_w > 1) then
	Resultado_w	:= Round(Resultado_w);
end if;

vl_retorno_p := resultado_w;

END;
$body$
