CREATE OR REPLACE PROCEDURE calcular_orc_avista ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, nr_seq_tabela_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_atualizacao_w			timestamp			:= clock_timestamp();
cd_centro_controle_w		integer 		:= 0;
cd_natureza_gasto_w		numeric(20) 		:= 0;
nr_sequencia_w			bigint;
vl_orcado_w			double precision 		:= 0;
vl_avista_w			double precision 		:= 0;
qt_dias_prazo_w			double precision 		:= 0;
pr_taxa_financ_w			double precision 		:= 0;
cd_comp_parametro_w		integer;

c01 CURSOR FOR
SELECT	nr_sequencia,
	vl_orcado,
	coalesce(qt_dias_prazo, 1)
from	orcamento_custo
where	nr_seq_tabela	= nr_seq_tabela_p;


BEGIN

select	coalesce(max(qt_parametro),0),
	max(cd_comp_parametro)
into STRICT	pr_taxa_financ_w,
	cd_comp_parametro_w
from	tabela_parametro
where	nr_seq_tabela		= nr_seq_tabela_p
and	nr_seq_parametro     = 3;

if (pr_taxa_financ_w = 0) and (cd_comp_parametro_w IS NOT NULL AND cd_comp_parametro_w::text <> '') then
	select	coalesce(max(qt_componente),0)
	into STRICT	pr_taxa_financ_w
	from	componente_data
	where	cd_estabelecimento = cd_estabelecimento_p
	and	cd_componente	= cd_comp_parametro_w
	and	dt_componente 	= (	SELECT	dt_mes_referencia
					from	tabela_custo
					where	nr_sequencia	= nr_seq_tabela_p);
end if;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	vl_orcado_w,
	qt_dias_prazo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	vl_avista_w              	:= vl_orcado_w / (1 + pr_taxa_financ_w /100)**(qt_dias_prazo_w/30);
	update	orcamento_custo
 	set	vl_avista		= vl_avista_w,
		dt_atualizacao	= dt_atualizacao_w,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_sequencia_w;
	end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
