SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE calcular_pto_audit_pront ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_resultado_W		varchar(3);
vl_item_w			double precision;
qt_total_ponto_w		double precision := 0;
qt_denominador_w		double precision := 0;
pr_conforme_w			double precision := 0;


C01 CURSOR FOR
	SELECT	a.ie_resultado,
		b.vl_item
	from	qua_auditoria_item b,
		qua_audit_Pront_result a
	where	a.nr_seq_audit_pront		= nr_sequencia_p
	and	a.nr_seq_item			= b.nr_sequencia
	and	(a.ie_resultado IS NOT NULL AND a.ie_resultado::text <> '');



BEGIN

qt_total_ponto_w	:= 0;
qt_denominador_w	:= 0;

open C01;
loop
fetch C01 into
	ie_resultado_W,
	vl_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	if (ie_resultado_W = 'NC') then
		qt_denominador_w	:= qt_denominador_w + vl_item_w;
	elsif (ie_resultado_W = 'C') then
		qt_denominador_w	:= qt_denominador_w + vl_item_w;
		qt_total_ponto_w	:= qt_total_ponto_w + vl_item_w;
	end if;
end loop;
close C01;

pr_conforme_w		:= dividir(qt_total_ponto_w * 100, qt_denominador_w);
update	qua_audit_prontuario
set	vl_total		= qt_total_ponto_w,
	vl_denominador	= qt_denominador_w,
	pr_conforme		= pr_conforme_w
where	nr_sequencia	= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
