CREATE OR REPLACE PROCEDURE calcular_qt_autoriz_laudo_apac ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_paciente_w		varchar(10);
dt_inicio_val_apac_w	timestamp;	
nr_seq_interno_w	bigint;
dt_fim_val_apac_w	timestamp;
qt_reg_mes_w		integer := 0;
qt_atualiza_mes_w	integer := 0;
qt_meses_aut_w		integer := 0;
dt_mes_atual_w		timestamp;

C01 CURSOR FOR 
	SELECT	b.cd_pessoa_fisica, 
		a.dt_inicio_val_apac, 
		a.nr_seq_interno, 
		a.dt_fim_val_apac, 
		coalesce(a.qt_meses_autorizado,0) 
	from	atendimento_paciente b, 
		sus_laudo_paciente a 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	a.nr_atendimento	= nr_atendimento_p 
	and	a.ie_tipo_laudo_apac	= 1 
	order by a.nr_seq_interno;


BEGIN 
 
open C01;
	loop 
	fetch C01 into 
		cd_paciente_w, 
		dt_inicio_val_apac_w, 
		nr_seq_interno_w, 
		dt_fim_val_apac_w, 
		qt_meses_aut_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		dt_mes_atual_w			:= dt_inicio_val_apac_w;
		 
		while(PKG_DATE_UTILS.start_of(dt_mes_atual_w,'month',0) <= PKG_DATE_UTILS.start_of(dt_fim_val_apac_w,'month',0)) loop 
			begin 
			select	count(*) 
			into STRICT	qt_reg_mes_w 
			from	paciente_atend_medic_adm	c, 
				paciente_atendimento		b, 
				paciente_setor			a 
			where	a.nr_seq_paciente		= b.nr_seq_paciente 
			and	b.nr_seq_atendimento		= c.nr_seq_atendimento 
			and	a.cd_pessoa_fisica		= cd_paciente_w 
			and	PKG_DATE_UTILS.start_of(dt_administracao,'month',0) = PKG_DATE_UTILS.start_of(dt_mes_atual_w,'month',0);
	 
			if (qt_reg_mes_w	> 0) then 
				qt_atualiza_mes_w	:= qt_atualiza_mes_w + 1;
			end if;
			dt_mes_atual_w	:= PKG_DATE_UTILS.ADD_MONTH(dt_mes_atual_w,1,0);
			end;
		end loop;
 
		qt_meses_aut_w	:= qt_meses_aut_w + qt_atualiza_mes_w;
 
		update	sus_laudo_paciente 
		set	qt_meses_autorizado	= qt_meses_aut_w, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_seq_interno		= nr_seq_interno_w 
		and	nr_atendimento		= nr_atendimento_p 
		and	qt_meses_autorizado 	= 0;
		 
		commit;
		 
		qt_meses_aut_w		:= 0;
		qt_atualiza_mes_w	:= 0;
 
	end loop;
close C01;
 
end;
$body$
