CREATE OR REPLACE PROCEDURE calcular_tot_disp_diluicao_md ( ie_param_controll_p bigint, qt_total_disp_dil_p INOUT bigint, nr_ocorrencia_dil_p INOUT bigint, qt_dose_especial_p bigint, ie_agrupador_dil_p bigint, qt_unitaria_dil_p INOUT bigint ) AS $body$
BEGIN

  if (ie_param_controll_p = 0) then

    qt_unitaria_dil_p := dividir_md(qt_total_disp_dil_p, nr_ocorrencia_dil_p);

    if (coalesce(qt_dose_especial_p, 0) > 0) then
      nr_ocorrencia_dil_p := nr_ocorrencia_dil_p - 1;
      if (ie_agrupador_dil_p = 9) then
        qt_total_disp_dil_p := qt_total_disp_dil_p - (qt_unitaria_dil_p * ceil(qt_dose_especial_p));
      else
        qt_total_disp_dil_p := coalesce(qt_total_disp_dil_p,0) - coalesce(ceil(qt_unitaria_dil_p),0);
      end if;
    end if;
  end if;

end;
$body$
