CREATE OR REPLACE PROCEDURE calcular_valor_ref_dil ( cd_material_p bigint) AS $body$
DECLARE



qt_referencia_w		double precision;
qt_concentracao_total_w	double precision;
qt_concentracao_ideal_w	double precision;
nr_sequencia_w		integer;

c01 CURSOR FOR
SELECT	nr_sequencia,
	qt_concentracao_total
from	material_diluicao
where	cd_material = cd_material_p
and	(qt_concentracao_total IS NOT NULL AND qt_concentracao_total::text <> '');


BEGIN

select	max(qt_concentracao_total)
into STRICT	qt_concentracao_total_w
from	material
where	cd_material	= cd_material_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	qt_concentracao_ideal_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_referencia_w	:= dividir(qt_concentracao_total_w, qt_concentracao_ideal_w);

	if (qt_referencia_w > 0) then

		update	material_diluicao
		set	qt_referencia = qt_referencia_w
		where	cd_material 	= cd_material_p
		and	nr_sequencia 	= nr_sequencia_w;
	end if;

	commit;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
