SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE calcular_vol_dil_prescr_mat ( nr_prescricao_p bigint, nr_agrupamento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_agrupamento_w		double precision;
nr_sequencia_princ_w		bigint;
nr_seq_mat_diluicao_w		bigint;
ie_subtrair_volume_medic_w	varchar(1);
qt_conversao_ml_w		double precision;
cd_material_dil_w		integer;
qt_dose_dil_w			double precision;
cd_unidade_medida_dose_dil_w	varchar(30);
qt_conversao_ml_dil_w		double precision;
nr_sequencia_dil_w		bigint;
Qt_volume_diluente_w		double precision;
qt_dose_ref_w			double precision;
qt_conv_W			double precision;
ie_ConsisteDoseConsumoMedic_w	varchar(1);
qt_dose_editada_w		double precision;
qt_qsp_diluente_w		double precision;
qt_volume_medic_w		double precision;
ie_alterou_dose_w		varchar(5);
nr_prescr_original_w	prescr_material.nr_prescricao_anterior%type;
nr_seq_original_w		prescr_material.nr_sequencia_anterior%type;


BEGIN

nr_agrupamento_w := nr_agrupamento_p;
select	min(nr_sequencia)
into STRICT	nr_sequencia_princ_w
from	prescr_material
where	ie_agrupador	= 1
and	nr_agrupamento	= nr_agrupamento_w
and	ie_item_superior = 'S'
and	nr_prescricao	= nr_prescricao_p;

if (coalesce(nr_sequencia_princ_w,0) = 0) then
	select	min(nr_sequencia)
	into STRICT	nr_sequencia_princ_w
	from	prescr_material
	where	ie_agrupador	= 1
	and	nr_agrupamento	= nr_agrupamento_w
	and	nr_prescricao	= nr_prescricao_p;
end if;

if (nr_sequencia_princ_w IS NOT NULL AND nr_sequencia_princ_w::text <> '') then

	select	coalesce(max(nr_seq_mat_diluicao),0),
		max(nr_sequencia),
		max(cd_material),
		max(qt_dose),
		max(cd_unidade_medida_dose),
		max(qt_dose_editada),
		max(qt_qsp_diluente),
		max(ie_alterou_dose_dil),
		max(nr_prescricao_anterior),
		max(nr_sequencia_anterior)
	into STRICT	nr_seq_mat_diluicao_w,
		nr_sequencia_dil_w,
		cd_material_dil_w,
		qt_dose_dil_w,
		cd_unidade_medida_dose_dil_w,
		qt_dose_editada_w,
		qt_qsp_diluente_w,
		ie_alterou_dose_w,
		nr_prescr_original_w,
		nr_seq_original_w
	from	prescr_material
	where	nr_sequencia_diluicao	=	nr_sequencia_princ_w
	and	nr_prescricao		=	nr_prescricao_p
	and	ie_agrupador		=	3;

	if ((coalesce(ie_alterou_dose_w::text, '') = '') and (nr_prescr_original_w > 0) and (nr_seq_original_w > 0))then

		select	max(ie_alterou_dose_dil)
		into STRICT	ie_alterou_dose_w
		from 	prescr_material
		where 	nr_prescricao = nr_prescr_original_w
		and		nr_sequencia = nr_seq_original_w;
	end if;

	ie_subtrair_volume_medic_w := 'N';

	if (qt_qsp_diluente_w > 0) then
		qt_volume_diluente_w		:= obter_dose_convertida(cd_material_dil_w, qt_qsp_diluente_w, cd_unidade_medida_dose_dil_w, obter_unid_med_usua('ml'));
		ie_subtrair_volume_medic_w	:= 'S';
		ie_ConsisteDoseConsumoMedic_w	:= 'N';
	elsif (nr_seq_mat_diluicao_w > 0) then
		Select 	coalesce(obter_dose_convertida(a.cd_material,a.qt_dose, a.cd_unidade_medida_dose, b.cd_unidade_medida_consumo),0)
		into STRICT	qt_dose_ref_w
		from	prescr_material a,
			material b
		where	a.cd_material = b.cd_material
		and 	a.nr_prescricao = nr_prescricao_p
		and 	a.nr_sequencia	= nr_sequencia_princ_w;

		begin
		select	coalesce(ie_subtrair_volume_medic,'N'),
			coalesce(obter_dose_convertida(cd_diluente,CASE WHEN  coalesce(ie_proporcao,'F')='F' THEN  qt_diluicao  ELSE qt_dose_ref_w*qt_referencia END ,cd_unid_med_diluente, cd_unidade_medida_dose_dil_w),0),
			coalesce(IE_DILUIR_INTEIRO,'N'),
			coalesce(qt_volume_medic,null)
		into STRICT	ie_subtrair_volume_medic_w,
			Qt_volume_diluente_w,
			ie_ConsisteDoseConsumoMedic_w,
			qt_volume_medic_w
		from	material_diluicao
		where	nr_seq_interno	=	nr_seq_mat_diluicao_w;
		exception
			when others then
			ie_subtrair_volume_medic_w := 'N';
			Qt_volume_diluente_w := null;
		end;
	end if;

	if (ie_subtrair_volume_medic_w = 'S') and
		((coalesce(qt_dose_editada_w::text, '') = '') or (qt_qsp_diluente_w > 0)) and (coalesce(ie_alterou_dose_w, 'N') <> 'S') then

		select	coalesce(obter_volume_ml_medic_agrup(nr_prescricao_p,nr_sequencia_princ_w, ie_ConsisteDoseConsumoMedic_w,qt_volume_medic_w),0)
		into STRICT	qt_conversao_ml_w
		;

		select	coalesce(obter_conversao_ml(cd_material_dil_w,coalesce(Qt_volume_diluente_w,qt_dose_dil_w),cd_unidade_medida_dose_dil_w),0)
		into STRICT	qt_conversao_ml_dil_w
		;

		Select	MAX(QT_CONVERSAO)
		into STRICT	qt_conv_W
		from	unidade_medida_dose_v
		where	cd_material		= cd_material_dil_w
		and 	UPPER(CD_UNIDADE_MEDIDA)	= UPPER(obter_unid_med_usua('ml'));

		if	((qt_conversao_ml_dil_w - qt_conversao_ml_w) > 0) then
			update	prescr_material
			set	qt_dose			=	qt_conversao_ml_dil_w - qt_conversao_ml_w,
				cd_unidade_medida_dose	=	obter_unid_med_usua('ml'),
				qt_conversao_dose	=	coalesce(qt_conv_W, qt_conversao_dose)
			where	nr_prescricao		=	nr_prescricao_p
			and	nr_sequencia		=	nr_sequencia_dil_w
			and	ie_agrupador		=	3;
		end if;

	end if;

end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
