CREATE OR REPLACE PROCEDURE calcular_vol_total_sol_onc ( Tipo_dosagem_p text, qt_tempo_aplicacao_p bigint, qt_solucao_total_p bigint, qt_dosagem_p bigint, NR_SEQ_PACIENTE_p bigint, nr_seq_solucao_p bigint, Campo_p bigint, vl_retorno_p INOUT bigint, ie_arredonda_p text) AS $body$
DECLARE




Gotas_W		double precision;
Tempo_W 	double precision;
Resultado_W	double precision;

ie_arredondar_w	varchar(1) := 'S';
ie_utiliza_disp_inf_w	varchar(1);


BEGIN


if (Tipo_dosagem_p = 'GTM') then
    begin
    Gotas_w	:= 20;
    Tempo_w	:= 60;
    end;

elsif (Tipo_dosagem_p = 'MLH') then
    begin
    Gotas_w	:= 1;
    Tempo_w	:= 1;
    end;

else
    begin
    Gotas_w	:= 50;
    Tempo_w	:= 60;
    end;
end if;

Resultado_w	:= 0;

if (campo_p = 1) then
    Resultado_w                               := Dividir(Dividir((Round(qt_solucao_total_p) * Gotas_w) ,
	                                             qt_tempo_aplicacao_p) , Tempo_w);
	if (Resultado_w > 1) then
		Resultado_w	:= Resultado_w;
	end if;

elsif (campo_p = 2) then
    begin
    Resultado_w                               := Round(Dividir((qt_dosagem_p * tempo_w * qt_tempo_aplicacao_p), Gotas_w));

    Resultado_w                               := (Round(Resultado_w / 100) * 100);

    end;

elsif (campo_p = 3) then
    Resultado_w                               := Round(Dividir(Dividir((qt_solucao_total_p * Gotas_w), qt_dosagem_p), Tempo_w ));

end if;

if (resultado_w = 0) then
	resultado_w := null;
end if;

select	coalesce(max(ie_bomba_infusao),'N')
into STRICT	ie_utiliza_disp_inf_w
from   	PACIENTE_PROTOCOLO_SOLUC
where  	NR_SEQ_PACIENTE = NR_SEQ_PACIENTE_p
and	nr_seq_solucao = nr_seq_solucao_p;

if (ie_arredonda_p = 'S') and (Tipo_dosagem_p = 'GTM') and (ie_utiliza_disp_inf_w = 'N') then

	resultado_w := round(resultado_w);
end if;

vl_retorno_p := resultado_w;

END;
$body$
