CREATE OR REPLACE PROCEDURE calc_atual_desc_bordero_pag (nr_sequencia_p bigint, nr_titulo_p bigint, dt_prev_pagto_p timestamp, nm_usuario_p text, ie_tipo_p text) AS $body$
DECLARE

/* ie_tipo_p
A	Forma antiga WCP
N	Forma nova DBP da tabela bordero_tit_pagar
*/
vl_desconto_w	double precision := 0;


BEGIN

vl_desconto_w := Calcular_Desc_Ant_Pagto(nr_titulo_p, dt_prev_pagto_p, vl_desconto_w, nm_usuario_p);

if (coalesce(vl_desconto_w,0) <> 0) then

	if (ie_tipo_p = 'A') then

		update	titulo_pagar
		set	vl_desconto_bordero 	= vl_desconto_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao 		= clock_timestamp()
		where	nr_titulo		= nr_titulo_p;

	elsif (ie_tipo_p = 'N') then

		update	bordero_tit_pagar
		set	vl_desconto_bordero 	= vl_desconto_w,
			nm_usuario		= nm_usuario_p,
			dt_atualizacao 		= clock_timestamp()
		where	nr_sequencia		= nr_sequencia_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
