CREATE OR REPLACE PROCEDURE calc_hydric_pediatric_act_md (Qt_Peso_p bigint, Qt_Peso_Calorico_p bigint, Qt_Descontar_Hidrico_p bigint, Qt_Nec_Hidrica_Diaria_p INOUT bigint, Qt_Aporte_Hidrico_Diario_p INOUT bigint ) AS $body$
BEGIN
   if (Qt_Peso_p <= 10) then
         Qt_Nec_Hidrica_Diaria_p    := dividir_sem_round_md((coalesce(Qt_Peso_p,0) * 100),Qt_Peso_Calorico_p);
         Qt_Aporte_Hidrico_Diario_p := ((Qt_Peso_p * 100) - coalesce(Qt_Descontar_Hidrico_p,0));

   elsif (Qt_Peso_p > 10 AND Qt_Peso_p <= 20) then
         Qt_Nec_Hidrica_Diaria_p    := dividir_sem_round_md(((1000 + ((coalesce(Qt_Peso_p,0) - 10) * 50))),Qt_Peso_Calorico_p);
         Qt_Aporte_Hidrico_Diario_p := (1000 + ((coalesce(Qt_Peso_p,0) - 10) * 50) - coalesce(Qt_Descontar_Hidrico_p,0));

   elsif (Qt_Peso_p > 20) then
         Qt_Nec_Hidrica_Diaria_p    := dividir_sem_round_md(((1500 + ((coalesce(Qt_Peso_p,0) - 20) * 20))),Qt_Peso_Calorico_p);
         Qt_Aporte_Hidrico_Diario_p := (1500 + ((coalesce(Qt_Peso_p,0) - 20) * 20) - coalesce(Qt_Descontar_Hidrico_p,0));
   end if;

END;
