CREATE OR REPLACE PROCEDURE calc_qt_unit_apresent_med_md ( ie_tpo_ret_p bigint, qt_a_lancar_p INOUT bigint, qt_mat_vetor_p INOUT bigint, qt_total_dispensar_p INOUT bigint, qt_unitaria_p INOUT bigint ) AS $body$
BEGIN
