        qt_unitaria_p := dividir_md(qt_mat_vetor_p, qt_unitaria_p);

    END IF;

    IF ( ie_tpo_ret_p = 2 ) THEN
        qt_a_lancar_p := ( coalesce(qt_a_lancar_p,0) - coalesce(qt_mat_vetor_p,0) );
        IF ( qt_a_lancar_p >= 0 ) THEN
            qt_total_dispensar_p := qt_a_lancar_p;
            IF ( qt_mat_vetor_p = 0 ) THEN
                qt_mat_vetor_p := qt_a_lancar_p;
            END IF;
        ELSE
            qt_mat_vetor_p := ( coalesce(qt_mat_vetor_p,0) - abs(coalesce(qt_a_lancar_p,0)) );
        END IF;

