CREATE OR REPLACE PROCEDURE calc_temp_insert_die_cpoe_md (dt_fim_p timestamp, qt_tempo_pausa_aux_p bigint, dt_fim_aux_p INOUT timestamp, qt_tempo_etapa_aux_p INOUT bigint, qt_tempo_aplic_aux_p INOUT bigint, dt_horario_p INOUT timestamp, nr_etapas_aux_p INOUT bigint ) AS $body$
BEGIN
    --- Inicio MD1
    dt_fim_aux_p := dt_horario_p + dividir_sem_round_md(qt_tempo_etapa_aux_p,24);

    if (dt_fim_aux_p > dt_fim_p) then
      dt_fim_aux_p  := dt_fim_p;
      qt_tempo_etapa_aux_p := (dt_fim_aux_p - dt_horario_p)*24;
    end if;

    dt_horario_p          := dt_fim_aux_p;
    qt_tempo_aplic_aux_p  := coalesce(qt_tempo_aplic_aux_p,0) + coalesce(qt_tempo_etapa_aux_p,0);
    dt_horario_p          := dt_horario_p + dividir_sem_round_md(qt_tempo_pausa_aux_p,24);
    nr_etapas_aux_p       := nr_etapas_aux_p-1;
    --- Fim MD1
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
