CREATE OR REPLACE PROCEDURE calc_uni_med_terap_prescr_md ( qt_dose_atual_p bigint, ie_unid_med_padrao_p text, cd_unidade_medida_out_w_p INOUT text, qt_dose_out_p INOUT bigint, cd_unidade_medida_out_p INOUT text, ds_delimitador_p text, qt_dose_out_aux_p bigint ) AS $body$
DECLARE

    nr_posicao_w          integer;
    nr_pos_inicial_w      integer;
    nr_pos_final_w        integer;
    nr_tam_delimitador_w  integer;
    qt_valor_w            varchar(200);

BEGIN
    IF ( coalesce(qt_dose_out_p::text, '') = '' )
        AND ( coalesce(qt_dose_atual_p, 0) = 0 )
        AND ( ie_unid_med_padrao_p = 'N' )
    THEN
        IF ( coalesce(cd_unidade_medida_out_p::text, '') = '' ) THEN
            IF length(cd_unidade_medida_out_w_p) > 0 THEN
                nr_posicao_w := 1;
                nr_pos_final_w := length(cd_unidade_medida_out_w_p) + 1;
                nr_tam_delimitador_w := length(ds_delimitador_p);
                LOOP
                    nr_pos_inicial_w := instr(cd_unidade_medida_out_w_p, ds_delimitador_p, nr_posicao_w);
                    IF nr_pos_inicial_w = 0 THEN
                        nr_pos_inicial_w := nr_pos_final_w;
                    END IF;
                    qt_valor_w := substr(cd_unidade_medida_out_w_p, nr_posicao_w, nr_pos_inicial_w - nr_posicao_w);
                    cd_unidade_medida_out_w_p := coalesce(qt_valor_w, 0);
                    EXIT WHEN nr_pos_inicial_w = nr_pos_final_w;
                    nr_posicao_w := nr_pos_inicial_w + nr_tam_delimitador_w;
                END LOOP;
                cd_unidade_medida_out_p := cd_unidade_medida_out_w_p;
            END IF;

        END IF;
        qt_dose_out_p	:= qt_dose_out_aux_p;
    END IF;
END;
$body$
