CREATE OR REPLACE PROCEDURE calc_vol_tot_prot_retorno ( cd_protocolo_p bigint, nr_seq_solucao_p bigint, qt_volume_p bigint, nr_sequencia_p bigint, qt_tempo_aplicacao_p bigint, ie_tipo_dosagem_p text, nr_etapas_p bigint, qt_hora_fase_p bigint, ie_calc_aut_p text, qt_volume_retorno_p INOUT bigint, qt_solucao_total_p INOUT bigint, qt_dosagem_p INOUT bigint) AS $body$
DECLARE




Gotas_W							double precision;
Tempo_W 						double precision;
Resultado_W						double precision;
nr_etapas_ww 					bigint;
qt_hora_fase_w					double precision;
ie_calc_aut_w					varchar(1) := 'N';
ie_esquema_alternado_w  		varchar(1) := 'N';
ie_tipo_dosagem_w	    		protocolo_medic_solucao.ie_tipo_dosagem%type;
qt_solucao_total_w	 			bigint;
qt_tempo_aplicacao_w			protocolo_medic_solucao.qt_tempo_aplicacao%type;


BEGIN

select	coalesce(qt_hora_fase_p, max(qt_hora_fase)),
		coalesce(nr_etapas_p, max(nr_etapas)),
		coalesce(ie_calc_aut_p, max(ie_calc_aut)),
		coalesce(max(ie_esquema_alternado),'N'),
		coalesce(ie_tipo_dosagem_p, max(ie_tipo_dosagem)),
		coalesce(qt_tempo_aplicacao_p, max(qt_tempo_aplicacao))
into STRICT	qt_hora_fase_w,
		nr_etapas_ww,
		ie_calc_aut_w,
		ie_esquema_alternado_w,
		ie_tipo_dosagem_w,
		qt_tempo_aplicacao_w
from   	protocolo_medic_solucao
where  	cd_protocolo   = cd_protocolo_p
and		nr_seq_solucao = nr_seq_solucao_p
and		nr_sequencia   = nr_sequencia_p;

if (upper(ie_tipo_dosagem_w) = 'GTM') then
    begin
    Gotas_w	:= 20;
    Tempo_w	:= 60;
    end;

elsif (upper(ie_tipo_dosagem_w) = 'MLH') then
    begin
    Gotas_w	:= 1;
    Tempo_w	:= 1;
    end;

else
    begin
    Gotas_w	:= 50;
    Tempo_w	:= 60;
    end;
end if;

Resultado_w	:= 0;

if (ie_calc_aut_w = 'S') then
	if (ie_esquema_alternado_w <> 'S') then
		qt_solucao_total_w	:= qt_volume_p * nr_etapas_ww;
	else
		qt_solucao_total_w	:= qt_volume_p;
	end if;

	Resultado_w  := Dividir(Dividir((Round(qt_solucao_total_w) * Gotas_w) ,
							qt_tempo_aplicacao_w) , Tempo_w);

	if (resultado_w = 0) then
		resultado_w := null;
	end if;


	qt_volume_retorno_p := qt_volume_p;
	qt_solucao_total_p	:= qt_solucao_total_w;
	qt_dosagem_p	:= resultado_w;

end if;

end;
$body$
