CREATE OR REPLACE PROCEDURE call_cancel_reverse_encounter ( nr_atendimento_p bigint, nm_usuario_p text, nr_seq_motivo_p bigint, cd_pessoa_fisica_p text, ds_observacao_p text) AS $body$
DECLARE


nr_seq_episodio_w		atendimento_paciente.nr_seq_episodio%type;
nr_seq_case_type_w		tipo_episodio.nr_sequencia%type;
ds_return_w			varchar(255);
qt_inpatient_encounters_w	smallint;
qt_outpatient_encounters_w 	smallint;

  item RECORD;

BEGIN

CALL cancelar_atendimento_paciente(	nr_atendimento_p,
				nm_usuario_p,
				nr_seq_motivo_p,
				cd_pessoa_fisica_p,
				ds_observacao_p);

if (coalesce(pkg_i18n.get_user_locale, 'pt_BR') in ('de_DE', 'de_AT')) then

	select 	max(nr_seq_episodio)
	into STRICT 	nr_seq_episodio_w
	from 	atendimento_paciente
	where 	nr_atendimento = nr_atendimento_p;

	for item in (	SELECT  ap.nr_atendimento
			from    atendimento_paciente ap
			where   (ap.dt_alta IS NOT NULL AND ap.dt_alta::text <> '')
			and     ap.nr_seq_episodio = nr_seq_episodio_w
			and	ap.ie_tipo_atendimento = 8
			and     ap.nr_atendimento <> nr_atendimento_p) loop

		ds_return_w := gerar_estornar_alta(	item.nr_atendimento, null, 0, 0, clock_timestamp(), nm_usuario_p, ds_return_w, 0, null, null, null, null);

	end loop;

	select 	count(1)
	into STRICT 	qt_inpatient_encounters_w
	from 	atendimento_paciente
	where 	nr_seq_episodio = nr_seq_episodio_w
	and	nr_atendimento <> nr_atendimento_p
	and 	coalesce(dt_cancelamento::text, '') = ''
	and	ie_tipo_atendimento = 1;

	if (qt_inpatient_encounters_w = 0) then
	
		select 	count(1)
		into STRICT 	qt_outpatient_encounters_w
		from 	atendimento_paciente
		where 	nr_seq_episodio = nr_seq_episodio_w
		and 	coalesce(dt_cancelamento::text, '') = ''
		and 	ie_tipo_atendimento = 8;

		if (qt_outpatient_encounters_w > 0) then

			select 	max(nr_sequencia)
			into STRICT 	nr_seq_case_type_w
			from 	tipo_episodio
			where 	ie_tipo = '8'
			and 	ie_situacao = 'A';

			if (nr_seq_case_type_w IS NOT NULL AND nr_seq_case_type_w::text <> '') then	

				update 	episodio_paciente
				set 	nr_seq_tipo_episodio = nr_seq_case_type_w
				where 	nr_sequencia = nr_seq_episodio_w;
				
			end if;
		end if;
	end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
