CREATE OR REPLACE PROCEDURE cancelar_ageexame_futura (cd_agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, cd_motivo_p text, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


dt_cancelamento_w		timestamp;
dt_atual_w			timestamp;
nr_seq_agenda_w		agenda_paciente.nr_sequencia%type;
cd_tipo_agenda_w	bigint;
qt_hor_cancel_w		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	agenda_paciente
	where	cd_agenda		= cd_agenda_p
	and	hr_inicio		>= dt_agenda_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p;


BEGIN

select	max(cd_tipo_agenda)
into STRICT	cd_tipo_agenda_w
from	agenda
where	cd_agenda	= cd_agenda_p;

open	c01;
loop
fetch	c01 into
	nr_seq_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	hr_inicio
	into STRICT	dt_atual_w
	from 	agenda_paciente
	where	nr_sequencia	= nr_seq_agenda_w;
	
	select	coalesce(max((to_char(hr_inicio,'ss'))::numeric ),0)+1
	into STRICT	qt_hor_cancel_w
	from	agenda_paciente
	where	cd_agenda = cd_agenda_p
	and	to_date(to_char(hr_inicio,'dd/mm/yyyy hh24:mi') || ':00', 'dd/mm/yyyy hh24:mi:ss') = to_date(to_char(dt_atual_w,'dd/mm/yyyy hh24:mi') || ':00', 'dd/mm/yyyy hh24:mi:ss')
	and	ie_status_agenda = 'C';

	update	agenda_paciente
	set	hr_inicio			= hr_inicio + qt_hor_cancel_w / 86400,
		ie_status_agenda		= 'C',
		nm_usuario_status		= nm_usuario_p,
		dt_status			= clock_timestamp(),
		ds_motivo_status		= ds_observacao_p,
		nm_usuario_cancel		= nm_usuario_p,
		dt_cancelamento			= clock_timestamp(),
		cd_motivo_cancelamento		= cd_motivo_p,
		nm_usuario			= nm_usuario_p
	where	nr_sequencia			= nr_seq_agenda_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
