CREATE OR REPLACE PROCEDURE cancelar_agendamento_hc ( cd_pessoa_fisica_p text, nr_seq_paciente_p text, ie_Acao_p text, ie_todos_p text, nm_usuario_p text, nr_seq_motivo_canc_p text, nr_seq_agenda_p text default '0') AS $body$
DECLARE


dt_agenda_w	varchar(4000);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then

	select 	to_char(dt_agenda,'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_agenda_w
	from	agenda_hc_paciente
	where	nr_sequencia = nr_seq_paciente_p;

	update	agenda_hc_paciente
	set	ie_status_agenda	= ie_acao_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		nr_seq_motivo_canc  = nr_seq_motivo_canc_p
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	((nr_seq_agenda = nr_seq_agenda_p) or (coalesce(nr_seq_agenda_p, '0') = '0'))
	and	((ie_todos_p = 'A' and dt_agenda =  to_date(dt_agenda_w,'dd/mm/yyyy hh24:mi:ss')) or (ie_todos_p  = 'P' and dt_agenda >= to_date(dt_agenda_w,'dd/mm/yyyy hh24:mi:ss')));
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
