CREATE OR REPLACE PROCEDURE cancelar_agendamento_js ( nr_seq_agenda_p bigint, cd_motivo_cancelamento_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '')then
	begin

	update	agenda_paciente
	set	dt_atualizacao	   	= clock_timestamp(),
		nm_usuario	   	= nm_usuario_p,
		ie_status_agenda   		= 'C',
		cd_motivo_cancelamento	= cd_motivo_cancelamento_p,
		dt_cancelamento	   	= clock_timestamp(),
		nm_usuario_cancel  	= nm_usuario_p
	where	nr_sequencia	   	= nr_seq_agenda_p;

	end;
end if;

commit;

end;
$body$
