CREATE OR REPLACE PROCEDURE cancelar_agenda_paciente_html ( nr_seq_agenda_p bigint, cd_motivo_p text, ds_observacao_p text, ie_cancela_agenda_consulta_p text default 'S') AS $body$
DECLARE


cd_estabelecimento_w			integer;
cd_perfil_w						integer;
nm_usuario_w					varchar(15);
ie_integracao_w				varchar(15);
ie_cancela_vaga_w				varchar(15);
ie_status_lista_espera_w	varchar(15);
nr_seq_lista_w					bigint;


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w				:= wheb_usuario_pck.get_cd_perfil;
nm_usuario_w			:= wheb_usuario_pck.get_nm_usuario;

ie_integracao_w := Obter_Param_Usuario(871, 257, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_integracao_w);
ie_cancela_vaga_w := Obter_Param_Usuario(871, 374, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_cancela_vaga_w);
ie_status_lista_espera_w := Obter_Param_Usuario(871, 662, cd_perfil_w, nm_usuario_w, cd_estabelecimento_w, ie_status_lista_espera_w);

select	coalesce(max(nr_seq_lista),0)
into STRICT		nr_seq_lista_w
from		agenda_paciente
where		nr_sequencia = nr_seq_agenda_p;

if (ie_status_lista_espera_w IS NOT NULL AND ie_status_lista_espera_w::text <> '') and (nr_seq_lista_w > 0) then
	CALL alterar_status_lista_espera(nm_usuario_w,nr_seq_lista_w,ie_status_lista_espera_w,'S');
end if;

CALL cancelar_agenda_paciente(nr_seq_agenda_p,0,nm_usuario_w,cd_motivo_p,ds_observacao_p, ie_cancela_agenda_consulta_p);

if (ie_integracao_w = 'D') then
	CALL verifica_envio_comunic_opme('C',cd_estabelecimento_w,nr_seq_agenda_p,nm_usuario_w,null);
end if;

if (ie_cancela_vaga_w = 'S') then
	CALL cancelar_gestao_vaga(nr_seq_agenda_p,cd_motivo_p,nm_usuario_w,cd_estabelecimento_w);
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
