CREATE OR REPLACE PROCEDURE cancelar_atendimento_futuro ( nr_sequencia_p bigint, nr_seq_motivo_canc_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
 
nr_seq_status_w 	bigint;
ie_susp_prescricao_w	varchar(1);
nr_prescricao_w		bigint;

c01 CURSOR FOR 
	SELECT	nr_prescricao 
	from	prescr_medica 
	where	nr_seq_atend_futuro = nr_sequencia_p 
	and	coalesce(dt_suspensao::text, '') = '';
					

BEGIN 
 
select 	max(nr_sequencia) 
into STRICT	nr_seq_status_w 
from  	status_atend_futuro 
where 	ie_status = 7;
 
 
update	atend_pac_futuro 
set	nr_seq_status	= nr_seq_status_w, 
	dt_cancelamento	= clock_timestamp(), 
	nm_usuario_cancelamento = nm_usuario_p, 
	nr_seq_motivo_cancelamento = nr_seq_motivo_canc_p 
where	nr_sequencia = nr_sequencia_p;
 
update	paciente_espera 
set	ie_situacao = 'C' 
where	nr_seq_atend_futuro = nr_sequencia_p;
 
ie_susp_prescricao_w := Obter_Valor_Param_Usuario(10000, 25, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);
 
if (ie_susp_prescricao_w = 'S') then 
	open c01;
	loop 
	fetch c01 into nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	 
		CALL Suspender_Prescricao(nr_prescricao_w, null, wheb_mensagem_pck.get_texto(791644), nm_usuario_p, 'N');
	 
	end loop;
	close c01;
	 
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
