SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_autor_agecons_item (nr_seq_proc_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proc_autor_w		bigint;
nr_sequencia_autor_w		bigint;
nr_seq_estagio_cancelamento_w	bigint;
ie_cancela_autor_w		varchar(15);

BEGIN
ie_cancela_autor_w := Obter_Param_Usuario(821, 424, obter_perfil_ativo, nm_usuario_p, Wheb_usuario_pck.Get_cd_estabelecimento, ie_cancela_autor_w);

if (coalesce(ie_cancela_autor_w,'N') = 'S') then

	select	max(a.nr_sequencia),
		max(b.nr_sequencia)
	into STRICT	nr_seq_proc_autor_w,
		nr_sequencia_autor_w
	from	procedimento_autorizado a,
		autorizacao_convenio b,
		estagio_autorizacao c
	where	a.nr_sequencia_autor = b.nr_sequencia
	and	b.nr_seq_estagio     = c.nr_sequencia
	and	c.ie_interno  	    <> '70'
	and	a.nr_seq_agenda_proc = nr_seq_proc_agenda_p;

	if (coalesce(nr_seq_proc_autor_w,0) > 0) then

		select	max(nr_sequencia)
		into STRICT	nr_seq_estagio_cancelamento_w
		from	estagio_autorizacao
		where	ie_interno		= '70'
		and	ie_situacao	= 'A'
		and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

		CALL atualizar_autorizacao_convenio(nr_sequencia_autor_w,nm_usuario_p,nr_seq_estagio_cancelamento_w,'N','N','S');

		update	procedimento_autorizado
		set	nm_usuario 	= nm_usuario_p,
			dt_atualizacao 	= clock_timestamp(),
			ds_observacao  	= substr(ds_observacao || chr(10) || chr(13) ||
					  wheb_mensagem_pck.get_texto(312553),1,2000)
		where	nr_sequencia	= nr_seq_proc_autor_w;
	end if;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
