CREATE OR REPLACE PROCEDURE cancelar_autor_conv_agenda ( nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_parametro_w		varchar(255);
nr_sequencia_w		bigint;
nr_seq_estagio_w	bigint;


BEGIN

vl_parametro_w := Obter_Param_Usuario(820, 142, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

if (vl_parametro_w = 'S') then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	autorizacao_convenio
	where	nr_seq_agenda  = nr_seq_agenda_p;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		begin
		select	max(nr_sequencia)
		into STRICT	nr_seq_estagio_w
		from	estagio_autorizacao
		where	ie_situacao = 'A'
		and	ie_interno  = 70
		and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

		CALL atualizar_autorizacao_convenio(nr_sequencia_w, nm_usuario_p, nr_seq_estagio_w, 'N', 'N', 'S');
		end;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
