SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_baixa_cheque_perda ( nr_seq_caixa_rec_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_fechamento_w		timestamp;
nr_seq_cheque_w		bigint;
nr_seq_caixa_w		bigint;
nr_seq_lote_w		bigint;
dt_fechamento_saldo_w	timestamp;
dt_cancelamento_w	timestamp;
nr_sequencia_w		bigint;
nr_seq_perda_w		bigint;

c01 CURSOR FOR 
SELECT	nr_sequencia, 
	nr_seq_cheque 
from	cheque_cr_perda 
where	nr_seq_caixa_rec = nr_seq_caixa_rec_p;

C02 CURSOR FOR 
SELECT	nr_sequencia 
from	perda_contas_receber 
where	nr_seq_cheque = nr_seq_cheque_w;


BEGIN 
 
-- edgar 29/01/2008 
lock table movto_trans_financ in exclusive mode;
lock table caixa_receb in exclusive mode;
 
select	dt_fechamento, 
	dt_cancelamento 
into STRICT	dt_fechamento_w, 
	dt_cancelamento_w 
from	caixa_receb 
where	nr_sequencia	= nr_seq_caixa_rec_p;
 
if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(217690);/*Este recebimento já foi cancelado!*/
end if;
 
if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then 
 
	select	b.nr_seq_caixa, 
		b.dt_fechamento 
	into STRICT	nr_seq_caixa_w, 
		dt_fechamento_saldo_w 
	from	caixa_saldo_diario b, 
		caixa_receb a 
	where	a.nr_seq_saldo_caixa	= b.nr_sequencia 
	and	a.nr_sequencia		= nr_seq_caixa_rec_p;
 
	if (dt_fechamento_saldo_w IS NOT NULL AND dt_fechamento_saldo_w::text <> '') then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(217689);/*O saldo diário deste recebimento já está fechado!*/
	end if;
 
	select	max(nr_seq_lote) 
	into STRICT	nr_seq_lote_w 
	from	movto_trans_financ 
	where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
	CALL estornar_lote_tesouraria(nr_seq_caixa_w, nr_seq_lote_w, nm_usuario_p, 'N', null);
 
	/* cancelar recebimento de caixa */
 
	/* necessário atualizar o cancelamento aqui para que o saldo do cheque possa ser atualizado corretamente */
 
	update	caixa_receb 
	set	dt_cancelamento	= clock_timestamp(), 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp() 
	where	nr_sequencia	= nr_seq_caixa_rec_p;
 
	open c01;
	loop 
	fetch c01 into 
		nr_sequencia_w, 
		nr_seq_cheque_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		insert into cheque_cr_perda( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_caixa_rec, 
			nr_seq_cheque, 
			ie_tipo_perda, 
			vl_perda) 
		SELECT	nextval('cheque_cr_perda_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_caixa_rec_p, 
			nr_seq_cheque, 
			ie_tipo_perda, 
			vl_perda * -1 
		from	cheque_cr_perda 
		where	nr_sequencia	= nr_sequencia_w;
		 
		open C02;
		loop 
		fetch C02 into	 
			nr_seq_perda_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			CALL fin_cancelar_perda(nr_seq_perda_w, nm_usuario_p);
		end loop;
		close C02;
 
		CALL atualizar_saldo_neg_cheque_cr(nr_seq_cheque_w,nm_usuario_p);
 
		CALL gerar_cheque_cr_hist(nr_seq_cheque_w,wheb_mensagem_pck.get_texto(305857) || nr_seq_caixa_rec_p || wheb_mensagem_pck.get_texto(305859),'N',nm_usuario_p);
 
		end;
	end loop;
	close c01;
else 
	delete	from	cheque_cr_perda 
	where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
	delete	from	caixa_receb 
	where	nr_sequencia	= nr_seq_caixa_rec_p;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cancelar_baixa_cheque_perda ( nr_seq_caixa_rec_p bigint, nm_usuario_p text) FROM PUBLIC;

