CREATE OR REPLACE PROCEDURE cancelar_ciclo_quimio ( nr_seq_pend_agenda_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_motivo_cancel_p text default null) AS $body$
DECLARE

 
nr_seq_atend_w 				bigint;
dt_agenda_w		 			timestamp;
nr_seq_agenda_w		 		bigint;
nr_seq_agenda_cons_w	 	bigint;
cd_agenda_w		 			bigint;
ie_cancelar_com_prescr_w  varchar(1);
ie_possui_prescricao_w	 	varchar(1);

 
C01 CURSOR FOR 
	SELECT 	a.nr_seq_atendimento, 
		a.dt_Agenda, 
		a.nr_sequencia, 
		CASE WHEN coalesce(b.nr_prescricao::text, '') = '' THEN 'N'  ELSE 'S' END  
	from 	agenda_quimio a, 
		paciente_atendimento b 
	where 	a.nr_seq_atendimento = b.nr_seq_atendimento 
	and 	b.nr_seq_pend_agenda = nr_seq_pend_agenda_p 
	and	a.ie_status_agenda not in ('C','E') 
	and	Qt_Obter_Se_Dia_Agendado(a.nr_seq_atendimento, coalesce(b.dt_real, b.dt_prevista)) = 'S';

BEGIN
 
ie_cancelar_com_prescr_w := Obter_param_Usuario(865, 191, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_cancelar_com_prescr_w);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_atend_w, 
	dt_agenda_w, 
	nr_seq_agenda_w, 
	ie_possui_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if ((ie_possui_prescricao_w = 'N') or (ie_cancelar_com_prescr_w = 'S')) then 
	 
		CALL Cancelar_Dia_Quimio(nr_seq_atend_w, nr_seq_motivo_cancel_p, nm_usuario_p, cd_estabelecimento_p, null, ds_motivo_cancel_p);
		 
	end if;			
	end;
		 
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
