CREATE OR REPLACE PROCEDURE cancelar_comunic_interna ( nr_sequencia_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_registro_w		bigint;


BEGIN

if	((coalesce(nr_sequencia_p,0) > 0) and (coalesce(nr_seq_motivo_cancel_p,0) > 0))then
	begin

	select	count(*)
	into STRICT	qt_registro_w
	from	motivo_cancelamento_ci
	where	nr_sequencia = nr_seq_motivo_cancel_p;

	if (qt_registro_w > 0)	then
		update	COMUNIC_INTERNA
		set	dt_atualizacao 		= 	clock_timestamp(),
			dt_cancelamento 		= 	clock_timestamp(),
			nr_seq_motivo_cancel	=	nr_seq_motivo_cancel_p,
			nm_usuario		=	nm_usuario_p
		where	nr_sequencia		=	nr_sequencia_p;
	end	if;

	end;
end 	if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
