SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_dia_quimio ( nr_seq_atend_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_sequencia_p bigint default null, ds_motivo_cancel_p text default null) AS $body$
DECLARE


nr_seq_pend_agenda_w	bigint;
dt_agenda_w		timestamp;
ie_cancelar_consulta_w	varchar(1);
nr_seq_agenda_cons_w	        agenda_quimio.nr_seq_agenda_cons%type;
cd_agenda_w		bigint;
ie_limpar_dt_real_w	varchar(1);			
ie_gerar_agenda_cons_w	varchar(1);
nr_seq_ag_quimio_w	agenda_quimio.nr_sequencia%type;


BEGIN

ie_cancelar_consulta_w := Obter_param_Usuario(865, 35, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_cancelar_consulta_w);
ie_limpar_dt_real_w := Obter_param_Usuario(865, 222, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_limpar_dt_real_w);

--Parametros da agenda de quimioterapia(Shift + F11)
select	max(coalesce(ie_gerar_agenda_cons,'N'))		
into STRICT	ie_gerar_agenda_cons_w
from	parametro_agenda_quimio
where	cd_estabelecimento = cd_estabelecimento_p;

if (coalesce(nr_sequencia_p::text, '') = '') then
	select 	max(nr_sequencia)
	into STRICT	nr_seq_ag_quimio_w
	from	agenda_quimio
	where	nr_seq_atendimento = nr_seq_atend_p
	and 	ie_status_agenda not in ('E', 'C');
else
	select 	max(nr_sequencia)
	into STRICT	nr_seq_ag_quimio_w
	from	agenda_quimio
	where	nr_sequencia = nr_sequencia_p
	and 	ie_status_agenda <> 'E';
end if;

if (nr_seq_ag_quimio_w IS NOT NULL AND nr_seq_ag_quimio_w::text <> '') then
	CALL Qt_Alterar_Status_Agenda(nr_seq_ag_quimio_w, 'C', nm_usuario_p, 'N', nr_seq_motivo_cancel_p, substr(coalesce(ds_motivo_cancel_p, wheb_mensagem_pck.get_texto(791648)),1,255), cd_estabelecimento_p, 'CANCELARDIAQUIMIO');


	if (ie_cancelar_consulta_w = 'S') then	
		
		select	max(a.nr_seq_agenda_cons),
			max(b.cd_agenda),
			max(a.nr_sequencia)
		into STRICT	nr_seq_agenda_cons_w,
			cd_agenda_w,
			nr_seq_ag_quimio_w
		from	agenda_quimio a,
			agenda_consulta b
		where	a.nr_seq_agenda_cons 	= b.nr_sequencia
		and	a.nr_seq_atendimento	= nr_seq_atend_p;
		
		CALL alterar_status_agenda_cons(
			cd_estabelecimento_p,
			cd_agenda_w,
			nr_seq_agenda_cons_w,
			'C',
			null,
			null,
			'N',
			nm_usuario_p,
			null);	
		
	end if;

	if (ie_gerar_agenda_cons_w = 'S') then

		select	max(a.nr_sequencia),
			max(a.cd_agenda)
		into STRICT	nr_seq_agenda_cons_w,
			cd_agenda_w
		from	agenda_consulta a,
			agenda_integrada_item b
		where	a.nr_sequencia = b.nr_seq_agenda_cons
		and	b.nr_seq_atendimento = nr_seq_atend_p;		
		
		CALL alterar_status_agenda_cons(
			cd_estabelecimento_p,
			cd_agenda_w,
			nr_seq_agenda_cons_w,
			'C',
			null,
			null,
			'N',
			nm_usuario_p,
			null);

	end if;

	if (ie_limpar_dt_real_w = 'S') and (coalesce(nr_seq_atend_p,0) > 0) then
		update	paciente_atendimento
		set	dt_real	 = NULL
		where	nr_seq_atendimento = nr_seq_atend_p;
	end if;
	COMMIT;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
