CREATE OR REPLACE PROCEDURE cancelar_email_pend_proc_aprov ( nr_seq_aprovacao_p bigint, nr_seq_proc_aprov_p bigint) AS $body$
DECLARE


nr_seq_envio_w		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	envio_email_compra_agrup a
where	coalesce(ie_cancelado,'N') = 'N'
and	nr_seq_aprovacao = nr_seq_aprovacao_p
and	nr_seq_proc_aprov = nr_seq_proc_aprov_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_envio_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	email_compra_agrup_dest
	set	dt_envio = clock_timestamp(),
		ie_enviado = 'S'
	where	nr_seq_envio = nr_seq_envio_w
	and	coalesce(ie_enviado,'N') = 'N';

	end;
end loop;
close c01;

/* NÃ£o pode ter commit*/

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
