CREATE OR REPLACE PROCEDURE cancelar_entr_item_transf_etq ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ds_observacao_p text, nr_seq_motivo_cancel_p bigint, ie_gerar_pendencia_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_material_w		double precision;
qt_cancelar_w		double precision;

qt_nf_saida_w		integer;
dt_prevista_entrega_w	timestamp;
dt_prevista_entrega_ww	timestamp;
qt_prevista_entrega_w	double precision;
pr_diferenca_w		double precision;
qt_diferenca_w		double precision;
nr_sequencia_w		bigint;
qt_entrega_w		integer;

ds_motivo_w		varchar(100);
ds_observacao_w		varchar(255);
cd_unidade_medida_w	varchar(30);
cd_material_w		integer;
ds_material_w		varchar(255);


BEGIN 
select	cd_material, 
	substr(obter_desc_material(cd_material),1,255), 
	cd_unidade_medida_compra 
into STRICT	cd_material_w, 
	ds_material_w, 
	cd_unidade_medida_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p;
 
CALL inserir_historico_ordem_compra( 
	nr_ordem_compra_p, 
	'S', 
	Wheb_mensagem_pck.get_Texto(298665), /*'Cancelamento de atendimento do item',*/
 
	substr(WHEB_MENSAGEM_PCK.get_texto(298667,'CD_MATERIAL_W='||cd_material_w||';DS_MATERIAL_W='||ds_material_w||';NR_ITEM_OCI_P='||NR_ITEM_OCI_P),1,4000), /*Foi cancelado o atendimento do item [#@NR_ITEM_OCI_P#@] (#@CD_MATERIAL_W#@) - #@DS_MATERIAL_W#@.*/
 
	nm_usuario_p);
 
 
CALL gerar_comunic_solic_transf(nr_ordem_compra_p,nr_item_oci_p,32,nm_usuario_p);
CALL gerar_email_solic_transf(nr_ordem_compra_p,nr_item_oci_p,42,nm_usuario_p);
 
select	coalesce(max(dt_prevista_entrega),clock_timestamp()) 
into STRICT	dt_prevista_entrega_ww 
from	ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p;
 
update 	ordem_compra_item_entrega 
set	qt_prevista_entrega = qt_real_entrega, 
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp() 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p 
and	coalesce(dt_cancelamento::text, '') = '' 
and 	coalesce(qt_real_entrega,0) > 0;
 
delete	FROM ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p 
and	coalesce(dt_cancelamento::text, '') = '' 
and 	coalesce(qt_real_entrega,0) = 0;
 
delete	FROM ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p 
and	(dt_cancelamento IS NOT NULL AND dt_cancelamento::text <> '');
 
delete 	FROM ordem_compra_item_cb 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p 
and	coalesce(nr_seq_nota::text, '') = '' 
and	ie_atende_recebe = 'A';
 
select	qt_material 
into STRICT	qt_material_w 
from	ordem_compra_item 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p;
 
select	coalesce(sum(qt_prevista_entrega),0), 
	coalesce(max(dt_prevista_entrega), dt_prevista_entrega_ww) 
into STRICT	qt_prevista_entrega_w, 
	dt_prevista_entrega_w 
from	ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p;
 
qt_nf_saida_w 		:= obter_qt_oci_trans_nota(nr_ordem_compra_p,nr_item_oci_p,'S');
pr_diferenca_w 		:= dividir(qt_nf_saida_w,qt_prevista_entrega_w);
qt_diferenca_w		:= (qt_nf_saida_w - qt_prevista_entrega_w);
 
if (qt_diferenca_w > 0) then 
	insert into	ordem_compra_item_entrega(nr_sequencia, 
		nr_ordem_compra, 
		nr_item_oci, 
		qt_prevista_entrega, 
		dt_prevista_entrega, 
		nm_usuario, 
		dt_atualizacao, 
		dt_cancelamento) 
	values (nextval('ordem_compra_item_entrega_seq'), 
		nr_ordem_compra_p, 
		nr_item_oci_p, 
		qt_diferenca_w, 
		dt_prevista_entrega_w, 
		nm_usuario_p, 
		clock_timestamp(), 
		null);
elsif (qt_diferenca_w < 0) then 
	begin 
	update 	ordem_compra_item_entrega 
	set	qt_real_entrega = (qt_real_entrega * pr_diferenca_w), 
		qt_prevista_entrega = (qt_prevista_entrega * pr_diferenca_w), 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_ordem_compra = nr_ordem_compra_p 
	and	nr_item_oci = nr_item_oci_p;
	end;
end if;
 
select	coalesce(sum(qt_prevista_entrega), 0), 
	coalesce(max(dt_prevista_entrega), dt_prevista_entrega_ww) 
into STRICT	qt_prevista_entrega_w, 
	dt_prevista_entrega_w 
from	ordem_compra_item_entrega 
where	nr_ordem_compra = nr_ordem_compra_p 
and	nr_item_oci = nr_item_oci_p;
 
qt_cancelar_w := (qt_material_w - qt_prevista_entrega_w);
 
if (nr_seq_motivo_cancel_p > 0) then 
	select	ds_motivo 
	into STRICT	ds_motivo_w 
	from	motivo_cancel_sc_oc 
	where	nr_sequencia = nr_seq_motivo_cancel_p;
end if;
 
ds_observacao_w := substr(WHEB_MENSAGEM_PCK.get_texto(298669,'DS_MOTIVO_W='||DS_MOTIVO_W||';DS_OBSERVACAO_P='||DS_OBSERVACAO_P),1,255);/*Entrega cancelada pelo motivo: #@DS_MOTIVO_W#@.Observação: #@DS_OBSERVACAO_P#@.*/
 
if (qt_cancelar_w > 0) then 
	begin 
	insert into	ordem_compra_item_entrega(nr_sequencia, 
		nr_ordem_compra, 
		nr_item_oci, 
		qt_prevista_entrega, 
		dt_prevista_entrega, 
		nm_usuario, 
		dt_atualizacao, 
		dt_cancelamento, 
		ds_observacao) 
	values (nextval('ordem_compra_item_entrega_seq'), 
		nr_ordem_compra_p, 
		nr_item_oci_p, 
		qt_cancelar_w, 
		dt_prevista_entrega_w, 
		nm_usuario_p, 
		clock_timestamp(), 
		clock_timestamp(), 
		ds_observacao_w);
		 
	if (ie_gerar_pendencia_p = 'S') then 
		begin		 
		insert into sup_pendencia_transf_estab( 
			nr_sequencia, 
			nr_ordem_compra, 
			nr_item_oci, 
			cd_unidade_medida, 
			qt_material, 
			nr_seq_motivo_cancel, 
			dt_atualizacao, 
			dt_atualizacao_nrec, 
			nm_usuario, 
			nm_usuario_nrec) 
		values (	nextval('sup_pendencia_transf_estab_seq'), 
			nr_ordem_compra_p, 
			nr_item_oci_p, 
			cd_unidade_medida_w, 
			qt_cancelar_w, 
			nr_seq_motivo_cancel_p, 
			clock_timestamp(), 
			clock_timestamp(), 
			nm_usuario_p, 
			nm_usuario_p);
		end;
	end if;
	end;
end if;
 
select	count(*) 
into STRICT	qt_entrega_w 
from	ordem_compra_item_entrega 
where	coalesce(dt_cancelamento::text, '') = '' 
and	coalesce(qt_real_entrega,0) < coalesce(qt_prevista_entrega,0) 
and	nr_ordem_compra = nr_ordem_compra_p;
 
if (qt_entrega_w = 0) then 
	update 	ordem_compra 
	set	dt_baixa = clock_timestamp() 
	where	nr_ordem_compra = nr_ordem_compra_p;
end if;
 
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
