CREATE OR REPLACE PROCEDURE cancelar_episodio ( nr_seq_episodio_p bigint, cd_pessoa_fisica_p text, nr_seq_motivo_p bigint, ds_observacao_p text, dt_cancelamento_p timestamp default null, nm_ish_p text default null) AS $body$
DECLARE


nr_seq_triagem_w 	triagem_pronto_atend.nr_sequencia%type;
nm_usuario_w		episodio_paciente.nm_usuario_cancelamento%type;
dt_cancelamento_w	episodio_paciente.dt_cancelamento%type;

c01 CURSOR FOR
SELECT	ep.nr_atendimento
from	atendimento_paciente ep
where	ep.nr_seq_episodio = nr_seq_episodio_p
and	coalesce(ep.dt_cancelamento::text, '') = ''
order by ep.ie_tipo_atendimento desc;
BEGIN

if (coalesce(nm_ish_p::text, '') = '') then
	nm_usuario_w 		:= obter_usuario_ativo;
	dt_cancelamento_w 	:= clock_timestamp();
else
	nm_usuario_w 		:= nm_ish_p;
	dt_cancelamento_w 	:= dt_cancelamento_p;
end if;

for c01_w in c01 loop
	begin

	select coalesce(max(nr_sequencia), 0)
	into STRICT 	nr_seq_triagem_w
	from 	triagem_pronto_atend
	where 	nr_atendimento = c01_w.nr_atendimento;

	if (nr_seq_triagem_w > 0) then
		update	triagem_pronto_atend
		set	dt_cancelamento = dt_cancelamento_w,
			ie_status_paciente = 'C',
      ds_justificativa_cancel = ds_observacao_p,
      nm_usuario_cancel = nm_usuario_w
		where	nr_sequencia = nr_seq_triagem_w;
	end if;

	CALL cancelar_atendimento_paciente(
		c01_w.nr_atendimento,
		nm_usuario_w,
		nr_seq_motivo_p,
		cd_pessoa_fisica_p,
		ds_observacao_p);

	delete from atend_convenio_end_cob
	where nr_atendimento = c01_w.nr_atendimento;

	end;
end loop;

	update	episodio_paciente
	set	dt_cancelamento		= dt_cancelamento_w,
		nm_usuario_cancelamento	= nm_usuario_w,
		ds_motivo_cancelamento	= ds_observacao_p,
		nr_seq_motivo_cancelamento	= nr_seq_motivo_p
	where	nr_sequencia		= nr_seq_episodio_p;


CALL ajustar_atend_controle(cd_pessoa_fisica_p, nm_usuario_w, null, nr_seq_episodio_p, 'C');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
