SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_item_processo_compra ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, ds_observacao_p text, nm_usuario_p text, qt_material_cancelado_p bigint default 0) AS $body$
DECLARE


ds_observacao_w			varchar(255);
ds_observacao_orig_w		varchar(255);
nr_cot_compra_w			bigint;
nr_item_cot_compra_w		bigint;
nr_solic_compra_w			bigint;
nr_item_solic_compra_w		bigint;
qt_material_cancelado_w		solic_compra_item.qt_material_cancelado%type;
qt_material_w		solic_compra_item.qt_material%type;


BEGIN

ds_observacao_w	:= substr(ds_observacao_p,1,255);

select	coalesce(max(nr_cot_compra),0),
	coalesce(max(nr_item_cot_compra),0),
	coalesce(max(nr_solic_compra),0),
	coalesce(max(nr_item_solic_compra),0)
into STRICT	nr_cot_compra_w,
	nr_item_cot_compra_w,
	nr_solic_compra_w,
	nr_item_solic_compra_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p;

if	((nr_solic_compra_w = 0) or (nr_item_solic_compra_w = 0)) and (nr_cot_compra_w > 0) and (nr_item_cot_compra_w > 0) then
	begin

	select	coalesce(max(nr_solic_compra),0),
		coalesce(max(nr_item_solic_compra),0)
	into STRICT	nr_solic_compra_w,
		nr_item_solic_compra_w
	from	cot_compra_item
	where	nr_cot_compra = nr_cot_compra_w
	and	nr_item_cot_compra = nr_item_cot_compra_w;

	end;
end if;

if (nr_solic_compra_w > 0) and (nr_item_solic_compra_w > 0) then
	begin
	
	select	ds_observacao,
		coalesce(qt_material_cancelado,0),
        coalesce(qt_material,0)
	into STRICT	ds_observacao_orig_w,
		qt_material_cancelado_w,
        qt_material_w
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_w
	and	nr_item_solic_compra = nr_item_solic_compra_w;

	if (ds_observacao_orig_w IS NOT NULL AND ds_observacao_orig_w::text <> '') then
		ds_observacao_w	:= substr(WHEB_MENSAGEM_PCK.get_texto(298672,'DS_OBSERVACAO_ORIG_W='||DS_OBSERVACAO_ORIG_W||';DS_OBSERVACAO_W='||DS_OBSERVACAO_W),1,255); /*#@DS_OBSERVACAO_ORIG_W#@ Item cancelado: #@DS_OBSERVACAO_W#@.*/
	end if;

	if (qt_material_cancelado_w + qt_material_cancelado_p >= qt_material_w) then
        update	solic_compra_item
        set	cd_motivo_baixa 	= 2,
            dt_baixa = clock_timestamp(),
            ds_observacao	= ds_observacao_w,
            qt_material_cancelado = qt_material_w
        where	nr_solic_compra = nr_solic_compra_w
        and	nr_item_solic_compra = nr_item_solic_compra_w;
    else
        update	solic_compra_item
        set	cd_motivo_baixa 	= 2,
            ds_observacao	= ds_observacao_w,
            qt_material_cancelado = qt_material_cancelado_w + qt_material_cancelado_p
        where	nr_solic_compra = nr_solic_compra_w
        and	nr_item_solic_compra = nr_item_solic_compra_w;
    end if;

	end;
end if;

/* Nao pode ter commit */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
