CREATE OR REPLACE PROCEDURE cancelar_item_req_solic ( nr_requisicao_p bigint, nr_seq_item_requisicao_p bigint, qt_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_atendimento_req_mat_w	timestamp;
cd_motivo_baixa_w		sup_motivo_baixa_req.nr_sequencia%type;


BEGIN
begin
select	dt_atendimento
into STRICT	dt_atendimento_req_mat_w
from	item_requisicao_material
where	nr_requisicao = nr_requisicao_p
and	nr_sequencia = nr_seq_item_requisicao_p;
exception
when others then
	dt_atendimento_req_mat_w	:=	null;
end;

if (nr_requisicao_p > 0) and (coalesce(dt_atendimento_req_mat_w::text, '') = '') then
	begin
	select	max(nr_sequencia)
	into STRICT	cd_motivo_baixa_w
	from	sup_motivo_baixa_req
	where	cd_motivo_baixa = 6;

	CALL baixar_item_req_motivo(nm_usuario_p, nr_requisicao_p, nr_seq_item_requisicao_p, cd_motivo_baixa_w, qt_material_p);
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
