CREATE OR REPLACE PROCEDURE cancelar_itens_etapa_checkup (nr_seq_checkup_etapa_p bigint, ds_itens_p text, nm_usuario_p text) AS $body$
DECLARE

 
lista_informacao_w		varchar(32000);
ie_contador_w			bigint	:= 0;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
ie_pos_traco_w			bigint;
lista_inf_w			varchar(100);
CD_PROTOCOLO_w			bigint;
nr_seq_medicao_w		integer;
nr_seq_material_w		integer;
nr_agrupamento_w		integer;
qt_registro_null_w		smallint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_seq_solucao_w		bigint;
nr_agrupamento_soluc_w		bigint;

nr_seq_interno_w		bigint;
nr_prescricao_w			bigint;
nr_sequencia_w			integer;

 

BEGIN 
 
lista_informacao_w	:= ds_itens_p;
 
 
while	(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') or 
	ie_contador_w > 200 loop 
	begin 
	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);	
	nr_seq_interno_w		:= substr(lista_informacao_w,1,ie_pos_virgula_w - 1);
	 
	/* Obter a sequÃªncia lida */
 
	if (ie_pos_virgula_w <> 0) then 
		begin		 
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end;
	end if;
 
	if (nr_seq_checkup_etapa_p > 0) then 
				 
		select nr_sequencia, 
			nr_prescricao 
		into STRICT	nr_sequencia_w, 
			nr_prescricao_w 
		from	prescr_procedimento 
		where	nr_seq_checkup_etapa = nr_seq_checkup_etapa_p 
		and	nr_seq_interno = nr_seq_interno_w;
	 
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
			CALL Suspender_item_Prescricao(nr_prescricao_w,nr_sequencia_w,null,null,'PRESCR_PROCEDIMENTO',nm_usuario_p,null,null);			
		end if;
		 
	end if;	
	end;
 
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
