CREATE OR REPLACE PROCEDURE cancelar_lote_prescr ( nr_seq_lote_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text, ds_observacao_p text, cd_perfil_p bigint default null) AS $body$
DECLARE


reg_integracao_p			gerar_int_padrao.reg_integracao;
nr_seq_historico_w	bigint;
cd_perfil_ativo_w	integer;
ds_maq_user_w		varchar(80);
nr_seq_gedi_atend_w	bigint;
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
cd_local_estoque_w		local_estoque.cd_local_estoque%type;
cd_setor_atendimento_w	setor_atendimento.cd_setor_atendimento%type;
ie_agrupamento_w		ap_lote.ie_agrupamento%type;


BEGIN

cd_perfil_ativo_w	:= obter_perfil_ativo;

if (cd_perfil_ativo_w = 0) then
	cd_perfil_ativo_w := cd_perfil_p;
end if;

select	substr(obter_inf_sessao(0) ||' - ' || obter_inf_sessao(1),1,80)
into STRICT	ds_maq_user_w	
;

update	ap_lote_item
set	dt_supensao	= clock_timestamp(),
	nm_usuario_susp	= nm_usuario_p
where	nr_seq_lote	= nr_seq_lote_p;

update	ap_lote
set	ie_status_lote		= 'C',
	dt_cancelamento		= clock_timestamp(),
	cd_perfil_cancel	= cd_perfil_ativo_w,
	ds_maquina_cancelamento = ds_maq_user_w,
	nm_usuario_cancelamento = nm_usuario_p,
	nr_seq_motivo_cancel	= nr_seq_motivo_cancel_p
where  	nr_sequencia		= nr_seq_lote_p;

select	coalesce(max(ie_agrupamento),'N')
into STRICT	ie_agrupamento_w
from 	ap_lote
where 	nr_sequencia = nr_seq_lote_p;

if (ie_agrupamento_w = 'S') then
	update	ap_lote
	set		ie_status_lote			= 'C',
			dt_cancelamento			= clock_timestamp(),
			cd_perfil_cancel		= cd_perfil_ativo_w,
			ds_maquina_cancelamento = ds_maq_user_w,
			nm_usuario_cancelamento = nm_usuario_p,
			nr_seq_motivo_cancel	= nr_seq_motivo_cancel_p
	where  	nr_lote_agrupamento		= nr_seq_lote_p
    and ie_status_lote = 'G'; 	
end if;

SELECT  MAX(a.nr_sequencia)
INTO STRICT    nr_seq_gedi_atend_w
FROM	gedi_medic_atend a,
	prescr_mat_hor b
WHERE   b.nr_sequencia 	= a.nr_seq_horario
AND	b.nr_seq_lote		=  nr_seq_lote_p;

if (nr_seq_gedi_atend_w IS NOT NULL AND nr_seq_gedi_atend_w::text <> '') then

	update  gedi_medic_atend
	set 	ie_status		   =  'S'
	where	nr_sequencia 	   = 	nr_seq_gedi_atend_w;
end if;	
	
		
select	nextval('ap_lote_historico_seq')
into STRICT	nr_seq_historico_w
;		
		
if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then

	insert into ap_lote_historico(
	nr_sequencia,			dt_atualizacao,
	nm_usuario,			nr_seq_lote,
	ds_evento,			ds_log)
	values (nr_seq_historico_w,		clock_timestamp(),
	nm_usuario_p,			nr_seq_lote_p,
	OBTER_DESC_EXPRESSAO(331148),			ds_observacao_p);
end if;	

select	max(b.cd_estabelecimento),
	max(a.cd_local_estoque),
	max(b.cd_setor_atendimento)
into STRICT	cd_estabelecimento_w,
	cd_local_estoque_w,
	cd_setor_atendimento_w
from	prescr_medica b,
	ap_lote a
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_sequencia = nr_seq_lote_p;

reg_integracao_p.cd_estab_documento	:= cd_estabelecimento_w;
reg_integracao_p.cd_local_estoque	:= cd_local_estoque_w;
reg_integracao_p.nr_seq_agrupador	:= nr_seq_lote_p;
reg_integracao_p.cd_setor_atendimento	:= cd_setor_atendimento_w;
reg_integracao_p := gerar_int_padrao.gravar_integracao('275', nr_seq_lote_p, nm_usuario_p, reg_integracao_p);

commit;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cancelar_lote_prescr ( nr_seq_lote_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text, ds_observacao_p text, cd_perfil_p bigint default null) FROM PUBLIC;

