CREATE OR REPLACE PROCEDURE cancelar_movto_cartao_cr_js ( dt_cancelamento_p timestamp, nr_sequencia_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	update  movto_cartao_cr
	set    	dt_cancelamento =  pkg_date_utils.start_of(dt_cancelamento_p, 'DD', 0),
        	nm_usuario	=  nm_usuario_p,
		dt_atualizacao  =  clock_timestamp(),
		ds_observacao  	=  ds_observacao_p || ' ' || wheb_mensagem_pck.get_texto(304652,'DT_PARAMETRO='||PKG_DATE_FORMATERS.TO_VARCHAR(clock_timestamp(), 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO)) --'Cancelada movimentação em ' || to_char(clock_timestamp(),'dd/mm/yyyy')
	where  	nr_sequencia   	=  nr_sequencia_p;
	commit;
	end;
end if;
end;
