CREATE OR REPLACE PROCEDURE cancelar_neces_vaga_atend ( nr_atendimento_p bigint) AS $body$
DECLARE



nr_sequencia_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	gestao_vaga
	where	nr_atendimento = nr_atendimento_p
	and	ie_status in ('A','R');


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	gestao_vaga
	set	ie_status	= 'C',
		ds_observacao	= OBTER_DESC_EXPRESSAO(728620)
	where	nr_sequencia	= nr_sequencia_w;

	end;
end loop;
close C01;

update	unidade_atendimento
set	ie_status_unidade	= 'L',
	cd_paciente_reserva	 = NULL,
	nm_usuario_reserva	 = NULL,
	nm_usuario		= obter_usuario_ativo,
	dt_Atualizacao		= clock_timestamp()
where	ie_status_unidade	= 'R'
and	cd_paciente_reserva	= obter_pessoa_atendimento(nr_atendimento_p, 'C');

update	unidade_atendimento
set	cd_paciente_reserva	 = NULL,
	nm_usuario_reserva	 = NULL,
	nm_usuario		= obter_usuario_ativo,
	dt_Atualizacao		= clock_timestamp()
where	cd_paciente_reserva	= obter_pessoa_atendimento(nr_atendimento_p, 'C');

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
