SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_nf (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_bordero_w bigint;


BEGIN
if ( coalesce(nr_sequencia_p,0) <> 0) then

  begin
  select  max(nr_bordero)
  into STRICT    nr_bordero_w
  from    bordero_recebimento
  where   nr_seq_nota_fiscal = nr_sequencia_p;

  exception when others then
    nr_bordero_w := null;
  end;

  if (nr_bordero_w IS NOT NULL AND nr_bordero_w::text <> '')  then
      CALL wheb_mensagem_pck.exibir_mensagem_abort(1085688,'NR_SEQ_BORDERO=' || nr_bordero_w);
  else
      update nota_fiscal
      set ie_situacao   = 9,
          nr_seq_protocolo   = NULL,
          nr_interno_conta  = NULL,
          nr_seq_exportada  = NULL,
          ie_status_envio   = NULL,
          nm_usuario  = nm_usuario_p,
          dt_atualizacao  = clock_timestamp(),
          dt_cancelamento  = clock_timestamp(),
          nm_usuario_cancel = nm_usuario_p
      where nr_sequencia   = nr_sequencia_p;

      CALL Gerar_Titulo_Pagar_NF(nr_sequencia_p, nm_usuario_p);

      CALL gerar_historico_nota_fiscal(nr_sequencia_p, nm_usuario_p, '1', wheb_mensagem_pck.get_texto(312554));

  end if;


end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cancelar_nf (nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

