CREATE OR REPLACE PROCEDURE cancelar_paciente_espera ( nr_sequencia_p bigint, nr_seq_motivo_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p

C	= Cancelar
D	= Desfazer cancelamento

*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'C') then

	update	paciente_espera
	set	dt_atualizacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_situacao		= 'C',
		nr_motivo_cancel	= nr_seq_motivo_p
	where	nr_sequencia		= nr_sequencia_p;

elsif (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'D') then

	update	paciente_espera
	set	dt_atualizacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_situacao		= 'A',
		nr_motivo_cancel	 = NULL
	where	nr_sequencia		= nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
