CREATE OR REPLACE PROCEDURE cancelar_pesquisa_sac ( nr_seq_pesquisa_p bigint, nm_usuario_p text, ie_acao_p text default '') AS $body$
BEGIN


if (nr_seq_pesquisa_p	> 0) then
	begin
	if	((coalesce(upper(ie_acao_p), 'C')) = 'C') then
		update	sac_pesquisa
		set	dt_cancelamento		= clock_timestamp(),
			nm_usuario_cancelamento	= nm_usuario_p
		where	nr_sequencia		= nr_seq_pesquisa_p;
	elsif	((coalesce(upper(ie_acao_p), 'C')) = 'D') then
		update	sac_pesquisa
		set	dt_cancelamento		 = NULL,
			nm_usuario_cancelamento	 = NULL
		where	nr_sequencia		= nr_seq_pesquisa_p;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
