CREATE OR REPLACE PROCEDURE cancelar_processo_compra ( nr_ordem_compra_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text, ds_obs_cancel_p text) AS $body$
DECLARE



nr_cot_compra_w		bigint;
nr_solic_compra_w		bigint;
ds_observacao_w		varchar(4000);
ds_obs_cancel_w		varchar(4000);

C01 CURSOR FOR
SELECT	nr_solic_compra
from	cot_compra_item
where	nr_cot_compra = nr_cot_compra_w;


BEGIN

ds_obs_cancel_w := substr(ds_obs_cancel_p,1,4000);

select	coalesce(max(nr_cot_compra),0)
into STRICT	nr_cot_compra_w
from  	ordem_compra_item
where	nr_ordem_Compra = nr_ordem_compra_p;

if (nr_cot_compra_w > 0) then
	begin

	select	coalesce(max(ds_observacao),'')
	into STRICT	ds_observacao_w
	from	cot_compra
	where	nr_cot_compra = nr_cot_compra_w;

	ds_observacao_w := substr(ds_observacao_w || ds_obs_cancel_w,1,4000);

	update	cot_compra
	set 	nr_seq_motivo_cancel 	= nr_seq_motivo_cancel_p,
		ds_observacao 		= ds_observacao_w
        where	nr_cot_compra 		= nr_cot_compra_w;

	CALL gerar_historico_cotacao(nr_cot_compra_w,Wheb_mensagem_pck.get_Texto(298432),Wheb_mensagem_pck.get_Texto(298433),'S',nm_usuario_p);

	open C01;
	loop
	fetch C01 into
		nr_solic_compra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(nr_solic_compra_w,0) <> 0)  then
			CALL cancelar_solic_compra(nr_solic_compra_w,nr_seq_motivo_cancel_p,nm_usuario_p,ds_obs_cancel_p, 'N');
		end if;
		end;
	end loop;
	close C01;
	end;
end if;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
