CREATE OR REPLACE PROCEDURE cancelar_proc_autor_agenda (cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_agenda_p bigint, cd_procedimento_old_p bigint, ie_commit_p text) AS $body$
DECLARE


nr_sequencia_autor_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	autorizacao_convenio a
where	a.nr_seq_agenda	= nr_seq_agenda_p
and	exists (select	1
		from	procedimento_autorizado x
		where	x.nr_sequencia_autor	= a.nr_sequencia
		and	x.cd_procedimento		= cd_procedimento_old_p);


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_autor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	update	procedimento_autorizado
	set	qt_solicitada		= 0,
		ds_observacao		= 'Cancelado pelo agendamento (Procedimento alterado)',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia_autor		= nr_sequencia_autor_w
	and	cd_procedimento		= cd_procedimento_old_p;

end loop;
close C01;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
