SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_projeto_desenv_tec ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_itens_w		bigint;
cont_proj_w 		varchar(1);
nr_seq_cronograma_w	bigint;
nr_seq_ordem_serv_w 	bigint;

c01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	man_ordem_servico a, 
		proj_cronograma b, 
		proj_cron_etapa c		 
	where	a.nr_seq_proj_cron_etapa = c.nr_sequencia 
	and	c.nr_seq_cronograma = b.nr_sequencia 
	and	b.nr_seq_proj = nr_sequencia_p;


BEGIN 
select	count(*) 
into STRICT	qt_itens_w 
from	com_cliente_hist 
where 	nr_seq_projeto = nr_sequencia_p 
and	nr_seq_tipo = 21;
 
if (qt_itens_w = 0) then 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(264361); -- Para cancelamento do projeto é necessário histórico do tipo cancelamento 
end if;
 
update	proj_projeto 
set	dt_cancelamento = clock_timestamp(), 
	nm_usuario_cancelamento = nm_usuario_p, 
	nr_seq_estagio = 44, 
	ie_status = 'F', 
	nm_usuario = nm_usuario_p, 
	dt_atualizacao = clock_timestamp() 
where 	nr_sequencia = nr_sequencia_p;
 
select 	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END  
into STRICT	cont_proj_w 
from 	proj_projeto 
where 	(nr_seq_tipo_projeto IS NOT NULL AND nr_seq_tipo_projeto::text <> '') 
and	nr_sequencia = nr_sequencia_p;
 
if (cont_proj_w = 'S') then 
 
	select 	max(nr_sequencia) 
	into STRICT 	nr_seq_cronograma_w 
	from	proj_cronograma 
	where 	nr_seq_proj = nr_sequencia_p;
	 
	open c01;
	loop 
	fetch c01 into 
		nr_seq_ordem_serv_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		CALL man_encerrar_os(nr_seq_ordem_serv_w, 9, nm_usuario_p, wheb_mensagem_pck.get_texto(320538)); --Projeto cancelado. 
	end loop;
		 
	CALL enviar_ci_aprov_cron(nr_seq_cronograma_w, 'C');
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cancelar_projeto_desenv_tec ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

