CREATE OR REPLACE PROCEDURE cancelar_same_cpi_solic ( nr_sequencia_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	update	same_cpi_solic
	set 	ie_status_solic = 		'C',
		nm_usuario = 			nm_usuario_p,
		dt_atualizacao = 		clock_timestamp(),
		dt_cancelamento = 		clock_timestamp(),
		nm_usuario_cancel = 		nm_usuario_p,
		ds_justificativa_cancelamento = ds_justificativa_p
	where 	nr_sequencia = nr_sequencia_p;
	end;
end if;
commit;
end;
$body$
