-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cancelar_solic_compra ( nr_solic_compra_p bigint, nr_seq_motivo_cancel_p bigint, nm_usuario_p text, ds_observacao_p text, ie_consiste_cot_p text default 'S') AS $body$
DECLARE


ie_empenho_orcamento_w		varchar(10);			
nr_sequencia_w			bigint;
nr_seq_classif_w			bigint;
nm_usuario_w			varchar(255);
ds_observacao_w			solic_compra.ds_observacao%type;
ds_motivo_cancel_w		varchar(80);
cd_perfil_w			integer;
cd_estabelecimento_w		smallint;
nr_cot_compra_w			bigint;
nr_ordem_compra_w		bigint;
ie_forma_exportar_w		varchar(1);
qt_aprovada_w			smallint;
qt_registro_w			bigint;

/* Campos para evio da comunicacao */

nr_seq_regra_w			bigint;
cd_perfil_ww			varchar(10);
ie_ci_lida_w			varchar(1);
nm_usuario_destino_w		varchar(255);
ds_titulo_w			varchar(80);
ds_comunic_w			varchar(4000);
nr_seq_comunic_w			bigint;
qt_regra_usuario_w			bigint;
cd_setor_atendimento_w		integer;
/* Se tiver setor na regra, envia CI para os setores */

ds_setor_adicional_w                    	varchar(2000) := '';
/* Campos da regra Usuario da Regra */

cd_setor_regra_usuario_w		integer;

/* Envio de email */

nr_seq_regra_email_w		bigint;
ds_email_adicional_w		varchar(2000);
ds_email_destino_w			varchar(255);
ds_email_usuario_aprov_w		varchar(255);
ds_assunto_w			varchar(80);
ds_mensagem_w			varchar(2000);
ds_email_origem_w			varchar(255);
ie_usuario_w			varchar(3);
cd_perfil_disparar_w		integer;
ds_usuario_origem_w		varchar(255);
cd_comprador_w			varchar(10);
ds_email_comprador_w		varchar(255);
ds_usuario_comprador_w		varchar(255);
ie_momento_envio_w		varchar(1);
ie_integracao_solic_w		varchar(15);
ds_erro_w			varchar(2000);
ie_envia_pessoa_deleg_w		varchar(1);
ds_email_remetente_w		varchar(255);
cd_setor_solicitante_w		setor_atendimento.cd_setor_atendimento%type;
ie_consiste_cot_w		varchar(1);

c04 CURSOR FOR	
SELECT	b.nr_sequencia,
	b.cd_perfil
from	regra_envio_comunic_compra a,
	regra_envio_comunic_evento b
where	a.nr_sequencia = b.nr_seq_regra
and	a.cd_funcao = 913
and	b.cd_evento = 28
and	b.ie_situacao = 'A'
and	a.cd_estabelecimento = cd_estabelecimento_w
and	((cd_setor_atendimento_w IS NOT NULL AND cd_setor_atendimento_w::text <> '' AND b.cd_setor_destino = cd_setor_atendimento_w) or
	((coalesce(cd_setor_atendimento_w::text, '') = '') and (coalesce(b.cd_setor_destino::text, '') = '')) or (coalesce(b.cd_setor_destino::text, '') = ''))
and	substr(obter_se_envia_ci_regra_compra(b.nr_sequencia,nr_solic_compra_p,'SC',obter_perfil_ativo,nm_usuario_p,null),1,1) = 'S';

c05 CURSOR FOR
SELECT	coalesce(a.cd_setor_atendimento,0) cd_setor_atendimento
from	regra_envio_comunic_usu a
where	a.nr_seq_evento = nr_seq_regra_w;

c06 CURSOR FOR
SELECT	a.ds_email
from	usuario a,
	pessoas_processo_aprovacao_v b
where	b.cd_pessoa_fisica = a.cd_pessoa_fisica
and	b.nr_sequencia in (SELECT	distinct
				x.nr_seq_aprovacao
			from	solic_compra_item x
			where	x.nr_solic_compra = nr_solic_compra_p)
group by	a.ds_email;

c07 CURSOR FOR
SELECT	ds_email
from	(SELECT	a.ds_email
	from	usuario a,
		pessoas_processo_aprovacao_v b
	where	b.cd_pessoa_fisica = a.cd_pessoa_fisica
	and	b.nr_sequencia(select	distinct
					x.nr_seq_aprovacao
				from	solic_compra_item x
				where	x.nr_solic_compra = nr_solic_compra_p)
	and	not exists (select 	1
			from	pessoa_fisica_delegacao x
			where	x.cd_pessoa_fisica = b.cd_pessoa_fisica
			and	((coalesce(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(x.dt_inicio_limite), ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())) <= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())) and (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(x.dt_limite) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp()))))
--and	((nvl(PKG_DATE_UTILS.start_of(c.dt_inicio_limite, 'dd', 0),PKG_DATE_UTILS.start_of(sysdate, 'dd', 0)) <= PKG_DATE_UTILS.start_of(sysdate, 'dd', 0)) and (PKG_DATE_UTILS.start_of(c.dt_limite, 'dd', 0) >= PKG_DATE_UTILS.start_of(sysdate, 'dd', 0))))
	
union all

	select	a.ds_email
	from	usuario a,
		pessoa_fisica_delegacao c,
		pessoas_processo_aprovacao_v b
	where	b.cd_pessoa_fisica = c.cd_pessoa_fisica
	and	c.cd_pessoa_substituta = a.cd_pessoa_fisica
    and	((coalesce(ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(c.dt_inicio_limite), ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())) <= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())) and (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(c.dt_limite) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp())))
	and	b.nr_sequencia in (select	distinct
					x.nr_seq_aprovacao
				from	solic_compra_item x
				where	x.nr_solic_compra = nr_solic_compra_p)
	) alias27
group by	ds_email;


BEGIN

select	coalesce(max(nr_cot_compra),0)
into STRICT	nr_cot_compra_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
and	coalesce(nr_seq_motivo_cancel::text, '') = ''
and	coalesce(obter_dados_cot_compra(nr_cot_compra,'M')::text, '') = '';

ie_consiste_cot_w	:= coalesce(ie_consiste_cot_p,'S');

if (nr_cot_compra_w > 0) and (ie_consiste_cot_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188140,'NR_COT_COMPRA=' || nr_cot_compra_w);
end if;

select	coalesce(min(a.nr_ordem_compra),0)
  into STRICT nr_ordem_compra_w
  from	ordem_compra a,
	ordem_compra_item b
 where	a.nr_ordem_compra = b.nr_ordem_compra
   and	coalesce(a.nr_seq_motivo_cancel::text, '') = ''
   and	b.nr_solic_compra = nr_solic_compra_p
   and substr(obter_se_item_oc_cancelado(b.nr_ordem_compra, b.nr_item_oci),1,1) = 'N';

if (nr_ordem_compra_w = 0) then
	select	coalesce(min(nr_ordem_compra),0)
	  into STRICT  nr_ordem_compra_w
	  from	cot_compra_solic_agrup
	 where	nr_solic_compra = nr_solic_compra_p
	   and	coalesce(obter_se_oc_cancelado(nr_ordem_compra), 'N') = 'N'
	   and 	nr_ordem_compra > 0;
end if;

if (nr_ordem_compra_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188141,'NR_ORDEM=' || nr_ordem_compra_w);
end if;

select	cd_estabelecimento,
	cd_setor_atendimento,
	cd_comprador_resp,
	obter_setor_usuario(obter_usuario_pessoa(cd_pessoa_solicitante))
into STRICT	cd_estabelecimento_w,
	cd_setor_atendimento_w,
	cd_comprador_w,
	cd_setor_solicitante_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

--parametros do empenho
select	coalesce(max(ie_empenho_orcamento),'N')
into STRICT	ie_empenho_orcamento_w
from	parametro_estoque
where	cd_estabelecimento	= cd_estabelecimento_w;



/*Envio do cancelamento ao ME*/

select	coalesce(max(ie_forma_exportar),'X')
into STRICT	ie_forma_exportar_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

if (ie_forma_exportar_w = '1') then	
	update	solic_compra
	set	ie_forma_exportar = '3'
	where	nr_solic_compra = nr_solic_compra_p;
end if;
/*if	(ie_forma_exportar_w = '0') then

	select	count(*)
	into	qt_aprovada_w
	from	solic_compra
	where	nr_solic_compra = nr_solic_compra_p
	and	dt_autorizacao is not null;	

	select	count(*)
	into	qt_registro_w
	from	registro_integr_compras
	where	nr_solic_compra = nr_solic_compra_p;
	
	if	(qt_aprovada_w > 0) and
		(qt_registro_w = 0) then
		wheb_mensagem_pck.exibir_mensagem_abort(188142);
	end if;
	
elsif	(ie_forma_exportar_w = '1') then	
	update	solic_compra
	set	ie_forma_exportar = '3'
	where	nr_solic_compra = nr_solic_compra_p;
end if;*/
select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

select	coalesce(max(cd_perfil_comunic_cancel),0),
	max(ie_integracao_solic)
into STRICT	cd_perfil_w,
	ie_integracao_solic_w
from	parametro_compras
where	cd_estabelecimento = cd_estabelecimento_w;

select	ds_motivo
into STRICT	ds_motivo_cancel_w
from	motivo_cancel_sc_oc
where	nr_sequencia = nr_seq_motivo_cancel_p;


ds_observacao_w :=	SUBSTR(WHEB_MENSAGEM_PCK.get_texto(298441,
				'NR_SOLIC_COMPRA_P='||NR_SOLIC_COMPRA_P||
				';NM_USUARIO_P='||NM_USUARIO_P||
				';DT_CANCELAMENTO_W='||PKG_DATE_FORMATERS_TZ.TO_VARCHAR(clock_timestamp(), 'timestamp', ESTABLISHMENT_TIMEZONE_UTILS.GETTIMEZONE)||
				';DS_MOTIVO_CANCEL_W='||DS_MOTIVO_CANCEL_W||
				';DS_OBSERVACAO_P='||DS_OBSERVACAO_P),1,4000);

			/*A solicitacao de compras #@NR_SOLIC_COMPRA_P#@ foi cancelada pelo usuario #@NM_USUARIO_P#@ em #@DT_CANCELAMENTO_W#@. 
			Motivo: #@DS_MOTIVO_CANCEL_W
			Observacao: #@DS_OBSERVACAO_P#@.*/
			
	
if (ie_integracao_solic_w = 'P') then

	delete from erros_integracao_piramide
	where	cd_Estabelecimento = cd_estabelecimento_w
	and	nm_usuario = nm_usuario_p
	and	ie_funcao = 'CSC';
	
	CALL exec_sql_dinamico('Tasy','begin pir_excluir_solic_compra(' 	|| nr_solic_compra_p || ','
									|| cd_estabelecimento_w || ','
									|| chr(39) || nm_usuario_p || chr(39) || '); end;');

	select	max(ds_erro)
	into STRICT	ds_erro_w
	from	erros_integracao_piramide
	where	cd_Estabelecimento = cd_estabelecimento_w
	and	nm_usuario = nm_usuario_p
	and	ie_funcao = 'CSC';

	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(188144,'DS_MENSAGEM=' || ds_erro_w);
	end if;
end if;		
			
update	solic_compra
set	nr_seq_motivo_cancel	= nr_seq_motivo_cancel_p,
	nm_usuario		= nm_usuario_p
where	nr_solic_compra		= nr_solic_compra_p;

/*Comunicacao interna*/

select	substr(obter_usuario_pessoa(cd_pessoa_solicitante) || ',' ||
	obter_usuario_pessoa(obter_pessoa_delegacao(cd_pessoa_solicitante, 'RC', clock_timestamp())),1,255)
into STRICT	nm_usuario_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

select	obter_classif_comunic('F')
into STRICT	nr_seq_classif_w
;

select	nextval('comunic_interna_seq')
into STRICT	nr_sequencia_w
;

insert	into comunic_interna(
	cd_estab_destino,
	dt_comunicado,
	ds_titulo, 
	ds_comunicado, 
	nm_usuario, 
	dt_atualizacao, 
	ie_geral,
	nm_usuario_destino,
	nr_sequencia, 
	ie_gerencial, 
	nr_seq_classif, 
	dt_liberacao,
	ds_perfil_adicional)
values (cd_estabelecimento_w,
	clock_timestamp(), 
	Wheb_mensagem_pck.get_Texto(298443, 'NR_SOLIC_COMPRA_W='||NR_SOLIC_COMPRA_P),
	ds_observacao_w, 
	Wheb_mensagem_pck.get_Texto(298446),
	clock_timestamp(),
	'N',
	nm_usuario_w || ',',
	nr_sequencia_w,
	'N',
	nr_seq_classif_w,
	clock_timestamp(),
	CASE WHEN cd_perfil_w=0 THEN ''  ELSE cd_perfil_w ||', ' END );

/* Envio da CI conforme regra (Comunicacao interna de compras) */

open C04;
loop
fetch C04 into	
	nr_seq_regra_w,
	cd_perfil_ww;
EXIT WHEN NOT FOUND; /* apply on C04 */
	begin
	
	select	count(*)
	into STRICT	qt_regra_usuario_w
	from	regra_envio_comunic_compra a,
		regra_envio_comunic_evento b,
		regra_envio_comunic_usu c
	where	a.nr_sequencia = b.nr_seq_regra
	and	b.nr_sequencia = c.nr_seq_evento
	and	b.nr_sequencia = nr_seq_regra_w;

	select	coalesce(ie_ci_lida,'N')
	into STRICT	ie_ci_lida_w
	from 	regra_envio_comunic_evento
	where 	nr_sequencia = nr_seq_regra_w;

	if (qt_regra_usuario_w > 0) then

		open C05;
		loop
		fetch C05 into
			cd_setor_regra_usuario_w;
		EXIT WHEN NOT FOUND; /* apply on C05 */
			begin
			if (cd_setor_regra_usuario_w <> 0) and (obter_se_contido_char(cd_setor_regra_usuario_w, ds_setor_adicional_w) = 'N') then
				ds_setor_adicional_w := substr(ds_setor_adicional_w || cd_setor_regra_usuario_w || ',',1,2000);
			end if;
			end;
		end loop;
		close C05;
		
		select	count(*)
		into STRICT	qt_regra_usuario_w
		from	regra_envio_comunic_compra a,
			regra_envio_comunic_evento b,
			regra_envio_comunic_usu c
		where	a.nr_sequencia = b.nr_seq_regra
		and	b.nr_sequencia = c.nr_seq_evento
		and	b.nr_sequencia = nr_seq_regra_w
		and	c.ie_usuario = 'SSS';
	
		if (qt_regra_usuario_w > 0) and (cd_setor_solicitante_w > 0) and (obter_se_contido_char(cd_setor_solicitante_w, ds_setor_adicional_w) = 'N') then
			
			ds_setor_adicional_w := substr(ds_setor_adicional_w || cd_setor_solicitante_w || ',',1,2000);
		end if;		

		nm_usuario_destino_w	:= '';	
		nm_usuario_destino_w	:= obter_usuarios_comunic_compras(nr_solic_compra_p,null,28,nr_seq_regra_w,'');
		ds_titulo_w		:= '';
		ds_comunic_w		:= '';			

		select	substr(obter_dados_regra_com_compra(nr_solic_compra_p, null, 913, 28 ,nr_seq_regra_w,'T'),1,2000) ds_titulo,
			substr(obter_dados_regra_com_compra(nr_solic_compra_p, null, 913, 28, nr_seq_regra_w,'M'),1,2000) ds_comunicacao
		into STRICT	ds_titulo_w,
			ds_comunic_w
		;

		ds_comunic_w := substr(replace_macro(ds_comunic_w, '@motivo', ds_motivo_cancel_w),1,2000);
		ds_comunic_w := substr(replace_macro(ds_comunic_w, '@observacao', ds_observacao_p),1,2000);

		if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then

			select	nextval('comunic_interna_seq')
			into STRICT	nr_seq_comunic_w
			;
			
			if (cd_perfil_ww IS NOT NULL AND cd_perfil_ww::text <> '') then
				cd_perfil_ww := cd_perfil_ww ||',';
			end if;
		
			insert	into comunic_interna(
				cd_estab_destino,				dt_comunicado,				ds_titulo,
				ds_comunicado,				nm_usuario,				dt_atualizacao,
				ie_geral,					nm_usuario_destino,			nr_sequencia,
				ie_gerencial,				nr_seq_classif,				dt_liberacao,
				ds_perfil_adicional,				ds_setor_adicional)
			values (	cd_estabelecimento_w,			clock_timestamp(),					ds_titulo_w,
				ds_comunic_w,				nm_usuario_p,				clock_timestamp(),
				'N',					nm_usuario_destino_w,			nr_seq_comunic_w,
				'N',					nr_seq_classif_w,				clock_timestamp(),
				cd_perfil_ww,				ds_setor_adicional_w);

			/*Para que a comunicacao seja gerada como lida ao proprio usuario */

			if (ie_ci_lida_w = 'S') then
				insert into comunic_interna_lida(nr_sequencia,nm_usuario,dt_atualizacao)values (nr_seq_comunic_w,nm_usuario_p,clock_timestamp());
			end if;
		end if;
	end if;	
	end;
end loop;
close C04;

/* Cancelar e-mails que estejam pendentes para a solicitacao*/

CALL sup_cancela_email_pendente(null,nr_solic_compra_p,'SC',cd_estabelecimento_w,nm_usuario_p);

/* Enviar EMAIL para APROVADOR da solicitacao de compra ( Solicitacao - Ao cancelar solicitacao (Avisa o(s) aprovador(es) ) */

select	max(ds_email),
	max(nm_guerra)
into STRICT	ds_email_comprador_w,
	ds_usuario_comprador_w
from	comprador
where	cd_pessoa_fisica = cd_comprador_w
and	cd_estabelecimento = cd_estabelecimento_w;

select	coalesce(max(nr_sequencia),0),
	max(replace(ds_email_adicional,',',';')),
	coalesce(max(ds_email_remetente),'X'),
	coalesce(max(ie_momento_envio),'I'),
	coalesce(max(ie_envia_pessoa_deleg),'N')
into STRICT	nr_seq_regra_email_w,
	ds_email_adicional_w,
	ds_email_remetente_w,
	ie_momento_envio_w,
	ie_envia_pessoa_deleg_w
from	regra_envio_email_compra
where	ie_tipo_mensagem = 49
and	ie_situacao = 'A'
and	cd_estabelecimento = cd_estabelecimento_w;

if (nr_seq_regra_email_w > 0) then
	begin

	select	substr(ds_assunto, 1, 80),
		substr(ds_mensagem_padrao, 1, 2000)
	into STRICT	ds_assunto_w,
		ds_mensagem_w
	from	regra_envio_email_compra
	where	nr_sequencia = nr_seq_regra_email_w;
	
	ds_assunto_w := substr(replace_macro(ds_assunto_w, '@solicitacao', nr_solic_compra_p),1,80);
	ds_assunto_w := substr(replace_macro(ds_assunto_w, '@motivo_cancelamento', ds_motivo_cancel_w),1,80);
	ds_assunto_w := substr(replace_macro(ds_assunto_w, '@observacao', ds_observacao_p),1,80);
	
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w, '@solicitacao', nr_solic_compra_p),1,2000);
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w, '@motivo_cancelamento', ds_motivo_cancel_w),1,2000);
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w, '@observacao', ds_observacao_p),1,2000);

	select	coalesce(max(ie_usuario),'U'),
		max(cd_perfil_disparar)
	into STRICT	ie_usuario_w,
		cd_perfil_disparar_w
	from	regra_envio_email_compra
	where	nr_sequencia = nr_seq_regra_email_w;

	if (ie_usuario_w = 'U') then --Usuario
		select	ds_email,
			nm_usuario
		into STRICT	ds_email_origem_w,
			ds_usuario_origem_w
		from	usuario
		where	nm_usuario = nm_usuario_p;
	elsif (ie_usuario_w = 'C') then --Setor compras
		select	ds_email
		into STRICT	ds_email_origem_w
		from	parametro_compras
		where	cd_estabelecimento = cd_estabelecimento_w;

		select	coalesce(ds_fantasia,ds_razao_social)
		into STRICT	ds_usuario_origem_w
		from	estabelecimento_v
		where	cd_estabelecimento = cd_estabelecimento_w;
	elsif (ie_usuario_w = 'O') then --Comprador
		ds_email_origem_w	:= ds_email_comprador_w;
		ds_usuario_origem_w := ds_usuario_comprador_w;
	end if;

	if (ie_envia_pessoa_deleg_w = 'S') then
		begin

		open C07;
		loop
		fetch C07 into	
			ds_email_usuario_aprov_w;
		EXIT WHEN NOT FOUND; /* apply on C07 */
			begin
			if (ds_email_usuario_aprov_w IS NOT NULL AND ds_email_usuario_aprov_w::text <> '') then
				ds_email_destino_w := ds_email_destino_w || ds_email_usuario_aprov_w || ';';
			end if;
			end;
		end loop;
		close C07;

		end;
	else
		begin

		open C06;
		loop
		fetch C06 into	
			ds_email_usuario_aprov_w;
		EXIT WHEN NOT FOUND; /* apply on C06 */
			begin
			if (ds_email_usuario_aprov_w IS NOT NULL AND ds_email_usuario_aprov_w::text <> '') then
				ds_email_destino_w := ds_email_destino_w || ds_email_usuario_aprov_w || ';';
			end if;
			end;
		end loop;
		close C06;

		end;
	end if;

	if (ds_email_adicional_w IS NOT NULL AND ds_email_adicional_w::text <> '') and
		((coalesce(cd_perfil_disparar_w::text, '') = '') or
		(cd_perfil_disparar_w IS NOT NULL AND cd_perfil_disparar_w::text <> '' AND cd_perfil_disparar_w = obter_perfil_ativo)) then
		ds_email_destino_w := ds_email_destino_w || ds_email_adicional_w || ';';
	end if;

	if (ds_email_remetente_w <> 'X') then
		ds_email_origem_w	:= ds_email_remetente_w;
	end if;

	if (ie_momento_envio_w = 'A') then
		begin

		CALL sup_grava_envio_email(
			'SC',
			'49',
			nr_solic_compra_p,
			null,
			null,
			ds_email_destino_w,
			ds_usuario_origem_w,
			ds_email_origem_w,
			ds_assunto_w,
			ds_mensagem_w,
			cd_estabelecimento_w,
			nm_usuario_p);

		end;
	else
		begin
		CALL enviar_email(ds_assunto_w,ds_mensagem_w,ds_email_origem_w,ds_email_destino_w,ds_usuario_origem_w,'M');
		exception when others then
			ds_assunto_w := '';
		end;
	end if;

	end;
end if;

CALL gerar_historico_solic_compra(
			nr_solic_compra_p,
			Wheb_mensagem_pck.get_Texto(298448),
			ds_observacao_w,
			'C',
			nm_usuario_p);

baixar_solic_compra(	nr_solic_compra_p, 1, ds_observacao_w, nm_usuario_p);

if (ie_empenho_orcamento_w = 'S') then
	CALL gerar_empenho_solic_compra(nr_solic_compra_p,nm_usuario_p);
end if;


if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
